// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "./ArbSys.sol";
import "./ArbGasInfo.sol";

// @title Chain
// @dev Wrap the calls to retrieve chain variables to handle differences
// between chain implementations
library Chain {
    // if the ARBITRUM_CHAIN_ID changes, a new version of this library
    // and contracts depending on it would need to be deployed
    uint256 public constant ARBITRUM_CHAIN_ID = 42161;
    uint256 public constant ARBITRUM_SEPOLIA_CHAIN_ID = 421614;

    ArbSys public constant arbSys = ArbSys(address(100));
    ArbGasInfo public constant arbGasInfo = ArbGasInfo(address(108));

    // @dev return the current block's timestamp
    // @return the current block's timestamp
    function currentTimestamp() internal view returns (uint256) {
        return block.timestamp;
    }

    // @dev return the current block's number
    // @return the current block's number
    function currentBlockNumber() internal view returns (uint256) {
        if (shouldUseArbPrecompiles()) {
            return arbSys.arbBlockNumber();
        }

        return block.number;
    }

    // @dev return the current block's hash
    // @return the current block's hash
    function getBlockHash(uint256 blockNumber) internal view returns (bytes32) {
        if (shouldUseArbPrecompiles()) {
            return arbSys.arbBlockHash(blockNumber);
        }

        return blockhash(blockNumber);
    }

    function shouldUseArbPrecompiles() internal view returns (bool) {
        return block.chainid == ARBITRUM_CHAIN_ID || block.chainid == ARBITRUM_SEPOLIA_CHAIN_ID;
    }

    function getCurrentTxL1GasFees() internal view returns (uint256) {
        if (!shouldUseArbPrecompiles()) {
            return 0;
        }
        return arbGasInfo.getCurrentTxL1GasFees();
    }
}

contracts/chain/ChainReader.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Strings.sol";

// @title ArbSys
// @dev Globally available variables for Arbitrum may have both an L1 and an L2
// value, the ArbSys interface is used to retrieve the L2 value
interface ArbSys {
    function arbBlockNumber() external view returns (uint256);

    function arbBlockHash(uint256 blockNumber) external view returns (bytes32);
}

contract ChainReader {
    ArbSys public constant arbSys = ArbSys(address(100));

    bytes32 public latestBlockHash;

    function updateLatestBlockHash(uint256 blockNumber) public {
        bytes32 blockHash = getBlockHash(blockNumber);
        if (blockHash == bytes32(0)) {
            revert(
                string.concat(
                    "blockHash is empty. blockNumber: ",
                    Strings.toString(blockNumber),
                    ", latest block number: ",
                    Strings.toString(getBlockNumber())
                )
            );
        }
        latestBlockHash = blockHash;
    }

    function updateLatestBlockHashWithDelay() public {
        updateLatestBlockHash(getBlockNumber() - 10);
    }

    function getBlockHash(uint256 blockNumber) public view returns (bytes32) {
        return arbSys.arbBlockHash(blockNumber);
    }

    function getBlockNumber() public view returns (uint256) {
        return arbSys.arbBlockNumber();
    }

    function getBlockHashWithDelayAndLatestBlockNumber(
        uint256 blockNumberDiff
    ) external view returns (bytes32, uint256) {
        return (arbSys.arbBlockHash(getBlockNumber() - blockNumberDiff), getBlockNumber());
    }

    function getBlockHashAndLatestBlockNumber(uint256 blockNumber) external view returns (bytes32, uint256) {
        return (getBlockHash(blockNumber), getBlockNumber());
    }
}

contracts/config/AutoCancelSyncer.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../data/DataStore.sol";
import "../data/Keys.sol";
import "../role/RoleModule.sol";
import "../event/EventEmitter.sol";
import "../utils/BasicMulticall.sol";
import "../order/OrderStoreUtils.sol";
import "../position/PositionStoreUtils.sol";
import "../order/AutoCancelUtils.sol";
import "../utils/Cast.sol";

contract AutoCancelSyncer is ReentrancyGuard, RoleModule, BasicMulticall {
    using EventUtils for EventUtils.AddressItems;
    using EventUtils for EventUtils.UintItems;
    using EventUtils for EventUtils.IntItems;
    using EventUtils for EventUtils.BoolItems;
    using EventUtils for EventUtils.Bytes32Items;
    using EventUtils for EventUtils.BytesItems;
    using EventUtils for EventUtils.StringItems;

    using Order for Order.Props;

    DataStore public immutable dataStore;
    EventEmitter public immutable eventEmitter;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter
    ) RoleModule(_roleStore) {
        dataStore = _dataStore;
        eventEmitter = _eventEmitter;
    }

    function syncAutoCancelOrderListForAccount(address account, uint256 start, uint256 end) external onlyConfigKeeper nonReentrant {
        bytes32[] memory positionKeys = PositionStoreUtils.getAccountPositionKeys(dataStore, account, start, end);

        for (uint256 i; i < positionKeys.length; i++) {
            _syncAutoCancelOrderListForPosition(account, positionKeys[i]);
        }
    }

    function syncAutoCancelOrderListForPosition(address account, bytes32 positionKey) external onlyConfigKeeper nonReentrant {
        _syncAutoCancelOrderListForPosition(account, positionKey);
    }

    function _syncAutoCancelOrderListForPosition(address account, bytes32 positionKey) internal {
        bytes32[] memory orderKeys = AutoCancelUtils.getAutoCancelOrderKeys(dataStore, positionKey);

        for (uint256 j; j < orderKeys.length; j++) {
            bytes32 orderKey = orderKeys[j];
            Order.Props memory order = OrderStoreUtils.get(dataStore, orderKey);

            if (order.account() == address(0) || (order.sizeDeltaUsd() == 0 && order.initialCollateralDeltaAmount() == 0)) {
                AutoCancelUtils.removeAutoCancelOrderKey(dataStore, positionKey, orderKey);

                EventUtils.EventLogData memory eventData;
                eventData.addressItems.initItems(1);
                eventData.addressItems.setItem(0, "account", account);
                eventData.bytes32Items.initItems(2);
                eventData.bytes32Items.setItem(0, "positionKey", positionKey);
                eventData.bytes32Items.setItem(1, "orderKey", orderKey);
                eventEmitter.emitEventLog1(
                    "ConfigSyncAutoCancelOrderList",
                    Cast.toBytes32(account),
                    eventData
                );
            }
        }
    }
}

contracts/config/Config.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../data/DataStore.sol";
import "../data/Keys.sol";
import "../role/RoleModule.sol";
import "../event/EventEmitter.sol";
import "../utils/BasicMulticall.sol";
import "../utils/Precision.sol";
import "../utils/Cast.sol";
import "../market/MarketUtils.sol";

import "./ConfigUtils.sol";

// @title Config
contract Config is ReentrancyGuard, RoleModule, BasicMulticall {
    using EventUtils for EventUtils.AddressItems;
    using EventUtils for EventUtils.UintItems;
    using EventUtils for EventUtils.IntItems;
    using EventUtils for EventUtils.BoolItems;
    using EventUtils for EventUtils.Bytes32Items;
    using EventUtils for EventUtils.BytesItems;
    using EventUtils for EventUtils.StringItems;

    uint256 public constant MAX_FEE_FACTOR = 5 * Precision.FLOAT_PRECISION / 100; // 5%

    // 0.00001% per second, ~315% per year
    uint256 public constant MAX_ALLOWED_MAX_FUNDING_FACTOR_PER_SECOND = 100000000000000000000000;
    // at this rate max allowed funding rate will be reached in 1 hour at 100% imbalance if max funding rate is 315%
    uint256 public constant MAX_ALLOWED_FUNDING_INCREASE_FACTOR_PER_SECOND = MAX_ALLOWED_MAX_FUNDING_FACTOR_PER_SECOND / 1 hours;
    // at this rate zero funding rate will be reached in 24 hours if max funding rate is 315%
    uint256 public constant MAX_ALLOWED_FUNDING_DECREASE_FACTOR_PER_SECOND = MAX_ALLOWED_MAX_FUNDING_FACTOR_PER_SECOND / 24 hours;
    // minimum duration required to fully distribute the position impact pool amount
    uint256 public constant MIN_POSITION_IMPACT_POOL_DISTRIBUTION_TIME = 7 days;

    DataStore public immutable dataStore;
    EventEmitter public immutable eventEmitter;

    // @dev the base keys that can be set
    mapping (bytes32 => bool) public allowedBaseKeys;
    // @dev the limited base keys that can be set
    mapping (bytes32 => bool) public allowedLimitedBaseKeys;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter
    ) RoleModule(_roleStore) {
        dataStore = _dataStore;
        eventEmitter = _eventEmitter;

        _initAllowedBaseKeys();
        _initAllowedLimitedBaseKeys();
    }

    modifier onlyKeeper() {
        if (
            !roleStore.hasRole(msg.sender, Role.LIMITED_CONFIG_KEEPER) &&
            !roleStore.hasRole(msg.sender, Role.CONFIG_KEEPER)
        ) {
            revert Errors.Unauthorized(msg.sender, "LIMITED / CONFIG KEEPER");
        }

        _;
    }

    function initOracleProviderForToken(address token, address provider) external onlyConfigKeeper nonReentrant {
        if (dataStore.getAddress(Keys.oracleProviderForTokenKey(token)) != address(0)) {
            revert Errors.OracleProviderAlreadyExistsForToken(token);
        }

        dataStore.setAddress(Keys.oracleProviderForTokenKey(token), provider);

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "token", token);
        eventData.addressItems.setItem(1, "provider", provider);
        eventEmitter.emitEventLog(
            "InitOracleProviderForToken",
            eventData
        );
    }

    function setOracleProviderForToken(address token, address provider) external onlyConfigKeeper nonReentrant {
        if (token == address(0)) {
            revert Errors.EmptyToken();
        }

        if (!dataStore.getBool(Keys.isOracleProviderEnabledKey(provider))) {
            revert Errors.InvalidOracleProvider(provider);
        }

        if (Chain.currentTimestamp() - dataStore.getUint(Keys.oracleProviderUpdatedAt(token, provider))
            < dataStore.getUint(Keys.ORACLE_PROVIDER_MIN_CHANGE_DELAY)) {
            revert Errors.OracleProviderMinChangeDelayNotYetPassed(token, provider);
        }

        dataStore.setUint(Keys.oracleProviderUpdatedAt(token, provider), Chain.currentTimestamp());
        dataStore.setAddress(Keys.oracleProviderForTokenKey(token), provider);

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "token", token);
        eventData.addressItems.setItem(1, "provider", provider);
        eventEmitter.emitEventLog(
            "SetOracleProviderForToken",
            eventData
        );
    }

    function setPriceFeed(
        address token,
        address priceFeed,
