        uint256 priceFeedHeartbeatDuration,
        uint256 stablePrice
    ) external onlyConfigKeeper nonReentrant {
        ConfigUtils.setPriceFeed(
            dataStore,
            eventEmitter,
            token,
            priceFeed,
            priceFeedMultiplier,
            priceFeedHeartbeatDuration,
            stablePrice
        );
    }

    function setDataStream(
        address token,
