            eventEmitter,
            token,
            feedId,
            dataStreamMultiplier,
            dataStreamSpreadReductionFactor,
            MAX_ALLOWED_MAX_FUNDING_FACTOR_PER_SECOND,
            MAX_ALLOWED_FUNDING_INCREASE_FACTOR_PER_SECOND,
            MAX_ALLOWED_FUNDING_DECREASE_FACTOR_PER_SECOND
        );
    }

