contracts/config/TimelockConfig.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import {Keys} from "../data/Keys.sol";
import {Errors} from "../error/Errors.sol";
import {EventEmitter} from "../event/EventEmitter.sol";
import {EventUtils} from "../event/EventUtils.sol";
import {RoleModule} from "../role/RoleModule.sol";
import {RoleStore} from "../role/RoleStore.sol";
import {DataStore} from "../data/DataStore.sol";
import {BasicMulticall} from "../utils/BasicMulticall.sol";
import {Precision} from "../utils/Precision.sol";
import {ConfigTimelockController} from "./ConfigTimelockController.sol";
import {OracleModule} from "../oracle/OracleModule.sol";
import {OracleUtils} from "../oracle/OracleUtils.sol";
import {MarketUtils} from "../market/MarketUtils.sol";

contract TimelockConfig is RoleModule, BasicMulticall {
    using EventUtils for EventUtils.AddressItems;
    using EventUtils for EventUtils.UintItems;
    using EventUtils for EventUtils.IntItems;
    using EventUtils for EventUtils.BoolItems;
    using EventUtils for EventUtils.Bytes32Items;
    using EventUtils for EventUtils.BytesItems;
    using EventUtils for EventUtils.StringItems;

    uint256 public constant MAX_TIMELOCK_DELAY = 5 days;

    EventEmitter public immutable eventEmitter;
    ConfigTimelockController public immutable timelockController;

    address public immutable dataStore;
    address public immutable oracleStore;

    constructor(
        EventEmitter _eventEmitter,
        address _dataStore,
        address _oracleStore,
        RoleStore _roleStore,
        ConfigTimelockController _timelockController
    ) RoleModule(_roleStore) {
        eventEmitter = _eventEmitter;
        dataStore = _dataStore;
        oracleStore = _oracleStore;
        timelockController = _timelockController;
    }

    // @dev signal granting of a role
    // @param account the account to grant the role
    // @param roleKey the role to grant
    function signalGrantRole(address account, bytes32 roleKey) external onlyTimelockAdmin {
        bytes memory payload = abi.encodeWithSignature("grantRole(address,bytes32)", account, roleKey);
        timelockController.schedule(address(roleStore), 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventData.bytes32Items.initItems(1);
        eventData.bytes32Items.setItem(0, "roleKey", roleKey);
        eventEmitter.emitEventLog(
            "SignalGrantRole",
            eventData
        );
    }

    // @dev signal revoking of a role
    // @param account the account to revoke the role for
    // @param roleKey the role to revoke
    function signalRevokeRole(address account, bytes32 roleKey) external onlyTimelockAdmin {

        bytes memory payload = abi.encodeWithSignature("revokeRole(address,bytes32)",
            account, roleKey);
        timelockController.schedule(address(roleStore), 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventData.bytes32Items.initItems(1);
        eventData.bytes32Items.setItem(0, "roleKey", roleKey);
        eventEmitter.emitEventLog(
            "SignalRevokeRole",
            eventData
        );
    }

    // @dev immediately revoke the role of an account
    // @param account the account to revoke the role for
    // @param roleKey the role to revoke
    function revokeRole(address account, bytes32 roleKey) external onlyTimelockMultisig {
        roleStore.revokeRole(account, roleKey);

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventData.bytes32Items.initItems(1);
        eventData.bytes32Items.setItem(0, "roleKey", roleKey);
        eventEmitter.emitEventLog(
            "RevokeRole",
            eventData
        );
    }

    function signalSetOracleProviderEnabled(address provider, bool value) external onlyTimelockAdmin {
        bytes memory payload = abi.encodeWithSignature("setBool(bytes32,bool)",
            Keys.isOracleProviderEnabledKey(provider), value);
        timelockController.schedule(dataStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "provider", provider);
        eventData.boolItems.initItems(1);
        eventData.boolItems.setItem(0, "value", value);
        eventEmitter.emitEventLog(
            "SignalSetOracleProviderEnabled",
            eventData
        );
    }

    function signalSetOracleProviderForToken(address token, address provider) external onlyTimelockAdmin {
        bytes memory payload = abi.encodeWithSignature("setAddress(bytes32,address)",
            Keys.oracleProviderForTokenKey(token), provider);
        timelockController.schedule(dataStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "token", token);
        eventData.addressItems.setItem(1, "provider", provider);
        eventEmitter.emitEventLog(
            "SignalSetOracleProviderForToken",
            eventData
        );
    }

    function signalSetAtomicOracleProvider(address provider, bool value) external onlyTimelockAdmin {
        bytes memory payload = abi.encodeWithSignature("setBool(bytes32,bool)",
            Keys.isAtomicOracleProviderKey(provider), value);
        timelockController.schedule(dataStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "provider", provider);
        eventData.boolItems.initItems(1);
        eventData.boolItems.setItem(0, "value", value);
        eventEmitter.emitEventLog(
            "SignalSetAtomicOracleProvider",
            eventData
        );
    }

    function signalAddOracleSigner(address account) external onlyTimelockAdmin {
        if (account == address(0)) {
            revert Errors.InvalidOracleSigner(account);
        }

        bytes memory payload = abi.encodeWithSignature("addSigner(address)", account);
        timelockController.schedule(oracleStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventEmitter.emitEventLog(
            "SignalAddOracleSigner",
            eventData
        );
    }

    function signalRemoveOracleSigner(address account) external onlyTimelockAdmin {
        if (account == address(0)) {
            revert Errors.InvalidOracleSigner(account);
        }

        bytes memory payload = abi.encodeWithSignature("removeSigner(address)", account);
        timelockController.schedule(oracleStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventEmitter.emitEventLog(
            "SignalRemoveOracleSigner",
            eventData
        );
    }

    // @dev signal setting of the fee receiver
    // @param account the new fee receiver
    function signalSetFeeReceiver(address account) external onlyTimelockAdmin {
        if (account == address(0)) {
            revert Errors.InvalidFeeReceiver(account);
        }

        bytes memory payload = abi.encodeWithSignature("setAddress(bytes32,address)",
            Keys.FEE_RECEIVER, account);
        timelockController.schedule(dataStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventEmitter.emitEventLog(
            "SignalSetFeeReceiver",
            eventData
        );
    }

    // @dev signal setting of the holding address
    // @param account of the new holding address
    function signalSetHoldingAddress(address account) external onlyTimelockAdmin {
        if (account == address(0)) {
            revert Errors.InvalidHoldingAddress(account);
        }

        bytes memory payload = abi.encodeWithSignature("setAddress(bytes32,address)",
            Keys.HOLDING_ADDRESS, account);
        timelockController.schedule(dataStore, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "account", account);
        eventEmitter.emitEventLog(
            "SignalSetHoldingAddress",
            eventData
        );
    }

    // @dev signal setting of a price feed
    // @param token the token to set the price feed for
    // @param priceFeed the address of the price feed
    // @param priceFeedMultiplier the multiplier to apply to the price feed results
    // @param stablePrice the stable price to set a range for the price feed results
    function signalSetPriceFeed(
        address token,
        address priceFeed,
        uint256 priceFeedMultiplier,
        uint256 priceFeedHeartbeatDuration,
        uint256 stablePrice
    ) external onlyTimelockAdmin {

        address[] memory targets = new address[](4);
        targets[0] = dataStore;
        targets[1] = dataStore;
        targets[2] = dataStore;
        targets[3] = dataStore;

        bytes[] memory payloads = new bytes[](4);
        payloads[0] = abi.encodeWithSignature("setAddress(bytes32,address)",
            Keys.priceFeedKey(token), priceFeed);
        payloads[1] = abi.encodeWithSignature("setUint(bytes32,uint256)",
            Keys.priceFeedMultiplierKey(token), priceFeedMultiplier);
        payloads[2] = abi.encodeWithSignature("setUint(bytes32,uint256)",
            Keys.priceFeedHeartbeatDurationKey(token), priceFeedHeartbeatDuration);
        payloads[3] = abi.encodeWithSignature("setUint(bytes32,uint256)",
            Keys.stablePriceKey(token), stablePrice);

        uint256[] memory values = new uint256[](4);
        timelockController.scheduleBatch(targets, values, payloads, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "token", token);
        eventData.addressItems.setItem(1, "priceFeed", priceFeed);
        eventData.uintItems.initItems(3);
        eventData.uintItems.setItem(0, "priceFeedMultiplier", priceFeedMultiplier);
        eventData.uintItems.setItem(1, "priceFeedHeartbeatDuration", priceFeedHeartbeatDuration);
        eventData.uintItems.setItem(2, "stablePrice", stablePrice);
        eventEmitter.emitEventLog(
            "SignalSetPriceFeed",
            eventData
        );
    }

    // @dev signal setting of a data stream feed
    // @param token the token to set the data stream feed for
    // @param feedId the ID of the data stream feed
    // @param dataStreamMultiplier the multiplier to apply to the data stream feed results
    // @param dataStreamSpreadReductionFactor the factor to apply to the data stream price spread
    function signalSetDataStream(
        address token,
        bytes32 feedId,
        uint256 dataStreamMultiplier,
        uint256 dataStreamSpreadReductionFactor
    ) external onlyTimelockAdmin {
        if (dataStreamSpreadReductionFactor > Precision.FLOAT_PRECISION) {
            revert Errors.ConfigValueExceedsAllowedRange(Keys.DATA_STREAM_SPREAD_REDUCTION_FACTOR, dataStreamSpreadReductionFactor);
        }

        address[] memory targets = new address[](3);
        targets[0] = dataStore;
        targets[1] = dataStore;
        targets[2] = dataStore;

        bytes[] memory payloads = new bytes[](3);
        payloads[0] = abi.encodeWithSignature("setBytes32(bytes32,bytes32)",
            Keys.dataStreamIdKey(token), feedId);
        payloads[1] = abi.encodeWithSignature("setUint(bytes32,uint256)",
            Keys.dataStreamMultiplierKey(token), dataStreamMultiplier);
        payloads[2] = abi.encodeWithSignature("setUint(bytes32,uint256)",
            Keys.dataStreamSpreadReductionFactorKey(token), dataStreamSpreadReductionFactor);

        uint256[] memory values = new uint256[](3);
        timelockController.scheduleBatch(targets, values, payloads, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(1);
        eventData.addressItems.setItem(0, "token", token);
        eventData.bytes32Items.initItems(1);
        eventData.bytes32Items.setItem(0, "feedId", feedId);
        eventData.uintItems.initItems(2);
        eventData.uintItems.setItem(0, "dataStreamMultiplier", dataStreamMultiplier);
        eventData.uintItems.setItem(1, "dataStreamSpreadReductionFactor", dataStreamSpreadReductionFactor);
        eventEmitter.emitEventLog(
            "SignalSetDataStream",
            eventData
        );
    }

    // @dev increase the timelock delay
    // @param the new timelock delay
    function increaseTimelockDelay(uint256 _timelockDelay) external onlyTimelockAdmin {
        if (_timelockDelay <= timelockController.getMinDelay()) {
            revert Errors.InvalidTimelockDelay(_timelockDelay);
        }

        if (_timelockDelay > MAX_TIMELOCK_DELAY) {
            revert Errors.MaxTimelockDelayExceeded(_timelockDelay);
        }

        bytes memory payload = abi.encodeWithSignature("updateDelay(uint256)", _timelockDelay);
        timelockController.schedule(address(timelockController), 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.uintItems.initItems(1);
        eventData.uintItems.setItem(0, "timelockDelay", _timelockDelay);
        eventEmitter.emitEventLog(
            "IncreaseTimelockDelay",
            eventData
        );
    }

    // @dev Withdraw funds from position impact pool(negative price impacts) and send them to `receiver`
    // @param market Market from withdraw
    // @param receiver Account to send funds from pool
    // @param amount Amount of tokens to withdraw
    function signalWithdrawFromPositionImpactPool(
        address market,
        address receiver,
        uint256 amount
    ) external onlyTimelockAdmin {
        if (market == address(0)) {
            revert Errors.EmptyMarket();
        }
        if (receiver == address(0)) {
            revert Errors.EmptyReceiver();
        }
        if (amount == 0) {
            revert Errors.EmptyPositionImpactWithdrawalAmount();
        }

        bytes memory payload = abi.encodeWithSignature(
            "withdrawFromPositionImpactPool(address,address,uint256)",
            market, receiver, amount);
        timelockController.schedule(address(timelockController), 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "market", market);
        eventData.addressItems.setItem(1, "receiver", receiver);
        eventData.uintItems.initItems(1);
        eventData.uintItems.setItem(0, "amount", amount);
        eventEmitter.emitEventLog(
            "SignalWithdrawFromPositionImpactPool",
            eventData
        );
    }

    function signalWithdrawTokens(
        address target,
        address token,
        address receiver,
        uint256 amount
    ) external onlyTimelockAdmin {
        if (target == address(0)) {
            revert Errors.EmptyTarget();
        }
        if (token == address(0)) {
            revert Errors.EmptyToken();
        }
        if (receiver == address(0)) {
            revert Errors.EmptyReceiver();
        }
        if (amount == 0) {
            revert Errors.EmptyWithdrawalAmount();
        }

        bytes memory payload = abi.encodeWithSignature(
            "withdrawTokens(address,address,uint256)",
            token,
            receiver,
            amount
        );
        timelockController.schedule(target, 0, payload, 0, 0, timelockController.getMinDelay());

        EventUtils.EventLogData memory eventData;

        eventData.addressItems.initItems(3);
        eventData.addressItems.setItem(0, "target", target);
        eventData.addressItems.setItem(1, "token", token);
        eventData.addressItems.setItem(1, "receiver", receiver);

        eventData.uintItems.initItems(1);
        eventData.uintItems.setItem(0, "amount", amount);

        eventEmitter.emitEventLog("SignalWithdrawTokens", eventData);
    }

    function execute(address target, bytes calldata payload) external onlyTimelockAdmin {
        timelockController.execute(target, 0, payload, 0, 0);
    }

    function executeWithOraclePrice(
        address target,
        bytes calldata payload,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external onlyTimelockAdmin {
        timelockController.executeWithOraclePrices(
            target, 0, payload, oracleParams
        );
    }

    function executeBatch(address[] calldata targets, uint256[] calldata values, bytes[] calldata payloads) external onlyTimelockAdmin {
        timelockController.executeBatch(targets, values, payloads, 0, 0);
    }

    function getHash(address target, bytes calldata payload) external view returns (bytes32) {
        return timelockController.hashOperation(target, 0, payload, 0, 0);
    }

    function getHashBatch(address[] calldata targets, bytes[] calldata payloads, uint256[] calldata values) external view returns (bytes32) {
        return timelockController.hashOperationBatch(targets, values, payloads, 0, 0);
    }

    function cancelAction(bytes32 id) external onlyTimelockAdmin {
        timelockController.cancel(id);
    }
}

contracts/contributor/ContributorHandler.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "../data/DataStore.sol";
import "../data/Keys.sol";
import "../role/RoleModule.sol";

import "../chain/Chain.sol";
import "../event/EventEmitter.sol";
import "../utils/BasicMulticall.sol";
import "../utils/Cast.sol";

// @title ContributorHandler
contract ContributorHandler is ReentrancyGuard, RoleModule, BasicMulticall {
    using SafeERC20 for IERC20;

    using EventUtils for EventUtils.AddressItems;
    using EventUtils for EventUtils.UintItems;
    using EventUtils for EventUtils.IntItems;
    using EventUtils for EventUtils.BoolItems;
    using EventUtils for EventUtils.Bytes32Items;
    using EventUtils for EventUtils.BytesItems;
    using EventUtils for EventUtils.StringItems;

    DataStore public immutable dataStore;
    EventEmitter public immutable eventEmitter;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter
    ) RoleModule(_roleStore) {
        dataStore = _dataStore;
        eventEmitter = _eventEmitter;
    }

    // note that since there will be account * token number of iterations in some functions
    // and that the CONTRIBUTOR_ACCOUNT_LIST and CONTRIBUTOR_TOKEN_LIST will be loaded entirely
    // into memory
    // care should be taken to not have too many accounts / tokens such that the block gas limit
    // or other execution limits is exceeded
    function addContributorAccount(address account) external nonReentrant onlyContributorKeeper {
        dataStore.addAddress(Keys.CONTRIBUTOR_ACCOUNT_LIST, account);
    }

    function removeContributorAccount(address account) external nonReentrant onlyContributorKeeper {
        dataStore.removeAddress(Keys.CONTRIBUTOR_ACCOUNT_LIST, account);
    }

    function addContributorToken(address token) external nonReentrant onlyContributorKeeper {
        dataStore.addAddress(Keys.CONTRIBUTOR_TOKEN_LIST, token);
    }

    function removeContributorToken(address token) external nonReentrant onlyContributorKeeper {
        dataStore.removeAddress(Keys.CONTRIBUTOR_TOKEN_LIST, token);
    }

    function setContributorTokenVault(address token, address vault) external nonReentrant onlyConfigKeeper {
        dataStore.setAddress(Keys.contributorTokenVaultKey(token), vault);
    }

    function setMinContributorPaymentInterval(uint256 interval) external nonReentrant onlyTimelockMultisig {
        // revert if < 20 days
        if (interval < 20 days) {
            revert Errors.MinContributorPaymentIntervalBelowAllowedRange(interval);
        }

        dataStore.setUint(Keys.MIN_CONTRIBUTOR_PAYMENT_INTERVAL, interval);
    }

    function setMaxTotalContributorTokenAmount(
        address[] memory tokens,
        uint256[] memory amounts
    ) external nonReentrant onlyTimelockMultisig {
        if (tokens.length != amounts.length) {
            revert Errors.InvalidSetMaxTotalContributorTokenAmountInput(tokens.length, amounts.length);
        }

        for (uint256 i; i < tokens.length; i++) {
            dataStore.setUint(Keys.maxTotalContributorTokenAmountKey(tokens[i]), amounts[i]);
        }
    }

    function sendPayments() external nonReentrant onlyContributorDistributor {
        uint256 lastPaymentAt = dataStore.getUint(Keys.CONTRIBUTOR_LAST_PAYMENT_AT);
        uint256 minPaymentInterval = dataStore.getUint(Keys.MIN_CONTRIBUTOR_PAYMENT_INTERVAL);

        if (lastPaymentAt + minPaymentInterval > Chain.currentTimestamp()) {
            revert Errors.MinContributorPaymentIntervalNotYetPassed(minPaymentInterval);
        }

        uint256 tokenCount = dataStore.getAddressCount(Keys.CONTRIBUTOR_TOKEN_LIST);
        uint256 accountCount = dataStore.getAddressCount(Keys.CONTRIBUTOR_ACCOUNT_LIST);

        address[] memory tokens = dataStore.getAddressValuesAt(Keys.CONTRIBUTOR_TOKEN_LIST, 0, tokenCount);
        address[] memory accounts = dataStore.getAddressValuesAt(Keys.CONTRIBUTOR_ACCOUNT_LIST, 0, accountCount);

        for (uint256 i; i < tokenCount; i++) {
            address token = tokens[i];
            address vault = dataStore.getAddress(Keys.contributorTokenVaultKey(token));

            for (uint256 j; j < accountCount; j++) {
                address account = accounts[j];
                uint256 amount = dataStore.getUint(Keys.contributorTokenAmountKey(account, token));

                IERC20(token).safeTransferFrom(vault, account, amount);

                EventUtils.EventLogData memory eventData;
                eventData.addressItems.initItems(2);
                eventData.addressItems.setItem(0, "account", account);
                eventData.addressItems.setItem(1, "token", token);
                eventData.uintItems.initItems(1);
                eventData.uintItems.setItem(0, "amount", amount);
                eventEmitter.emitEventLog1(
                    "SendContributorPayment",
                    Cast.toBytes32(account),
                    eventData
                );
            }
        }

        dataStore.setUint(Keys.CONTRIBUTOR_LAST_PAYMENT_AT, Chain.currentTimestamp());
    }

    function setContributorAmount(
        address account,
        address[] memory tokens,
        uint256[] memory amounts
    ) external nonReentrant onlyContributorKeeper {
        if (tokens.length != amounts.length) {
            revert Errors.InvalidSetContributorPaymentInput(tokens.length, amounts.length);
        }

        for (uint256 i; i < tokens.length; i++) {
            address token = tokens[i];
            if (!dataStore.containsAddress(Keys.CONTRIBUTOR_TOKEN_LIST, token)) {
                revert Errors.InvalidContributorToken(token);
            }

            uint256 amount = amounts[i];
            dataStore.setUint(Keys.contributorTokenAmountKey(account, token), amount);

            EventUtils.EventLogData memory eventData;
            eventData.addressItems.initItems(2);
            eventData.addressItems.setItem(0, "account", account);
            eventData.addressItems.setItem(1, "token", token);
            eventData.uintItems.initItems(1);
            eventData.uintItems.setItem(0, "amount", amount);
            eventEmitter.emitEventLog1(
                "SetContributorAmount",
                Cast.toBytes32(account),
                eventData
            );
        }

        _validateMaxContributorTokenAmounts();
    }

    // note that this is just a sanity validation since the maxTotalContributorTokenAmount
    // can technically be exceeded since can be separately updated in Config
    function _validateMaxContributorTokenAmounts() internal view {
        uint256 tokenCount = dataStore.getAddressCount(Keys.CONTRIBUTOR_TOKEN_LIST);
        uint256 accountCount = dataStore.getAddressCount(Keys.CONTRIBUTOR_ACCOUNT_LIST);

        address[] memory tokens = dataStore.getAddressValuesAt(Keys.CONTRIBUTOR_TOKEN_LIST, 0, tokenCount);
        address[] memory accounts = dataStore.getAddressValuesAt(Keys.CONTRIBUTOR_ACCOUNT_LIST, 0, accountCount);

        for (uint256 i; i < tokenCount; i++) {
            address token = tokens[i];
            uint256 totalAmount;

            for (uint256 j; j < accountCount; j++) {
                address account = accounts[j];
                uint256 amount = dataStore.getUint(Keys.contributorTokenAmountKey(account, token));
                totalAmount += amount;
            }

            uint256 maxTotalAmount = dataStore.getUint(Keys.maxTotalContributorTokenAmountKey(token));
            if (totalAmount > maxTotalAmount) {
                revert Errors.MaxTotalContributorTokenAmountExceeded(token, totalAmount, maxTotalAmount);
            }
        }
    }
}

contracts/data/DataStore.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../role/RoleModule.sol";
import "../utils/Calc.sol";

// @title DataStore
// @dev DataStore for all general state values
contract DataStore is RoleModule {
    using SafeCast for int256;

    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.AddressSet;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableValues for EnumerableSet.Bytes32Set;
    using EnumerableValues for EnumerableSet.AddressSet;
    using EnumerableValues for EnumerableSet.UintSet;

    // store for uint values
    mapping(bytes32 => uint256) public uintValues;
    // store for int values
    mapping(bytes32 => int256) public intValues;
    // store for address values
    mapping(bytes32 => address) public addressValues;
    // store for bool values
    mapping(bytes32 => bool) public boolValues;
    // store for string values
    mapping(bytes32 => string) public stringValues;
    // store for bytes32 values
    mapping(bytes32 => bytes32) public bytes32Values;

    // store for uint[] values
    mapping(bytes32 => uint256[]) public uintArrayValues;
    // store for int[] values
    mapping(bytes32 => int256[]) public intArrayValues;
    // store for address[] values
    mapping(bytes32 => address[]) public addressArrayValues;
    // store for bool[] values
    mapping(bytes32 => bool[]) public boolArrayValues;
    // store for string[] values
    mapping(bytes32 => string[]) public stringArrayValues;
    // store for bytes32[] values
    mapping(bytes32 => bytes32[]) public bytes32ArrayValues;

    // store for bytes32 sets
    mapping(bytes32 => EnumerableSet.Bytes32Set) internal bytes32Sets;
    // store for address sets
    mapping(bytes32 => EnumerableSet.AddressSet) internal addressSets;
    // store for uint256 sets
    mapping(bytes32 => EnumerableSet.UintSet) internal uintSets;

    constructor(RoleStore _roleStore) RoleModule(_roleStore) {}

    // @dev get the uint value for the given key
    // @param key the key of the value
    // @return the uint value for the key
    function getUint(bytes32 key) external view returns (uint256) {
        return uintValues[key];
    }

    // @dev set the uint value for the given key
    // @param key the key of the value
    // @param value the value to set
    // @return the uint value for the key
    function setUint(bytes32 key, uint256 value) external onlyController returns (uint256) {
        uintValues[key] = value;
        return value;
    }

    // @dev delete the uint value for the given key
    // @param key the key of the value
    function removeUint(bytes32 key) external onlyController {
        delete uintValues[key];
    }

    // @dev add the input int value to the existing uint value
    // @param key the key of the value
    // @param value the input int value
    // @return the new uint value
    function applyDeltaToUint(bytes32 key, int256 value, string memory errorMessage) external onlyController returns (uint256) {
        uint256 currValue = uintValues[key];
        if (value < 0 && (-value).toUint256() > currValue) {
            revert(errorMessage);
        }
        uint256 nextUint = Calc.sumReturnUint256(currValue, value);
        uintValues[key] = nextUint;
        return nextUint;
    }

    // @dev add the input uint value to the existing uint value
    // @param key the key of the value
    // @param value the input int value
    // @return the new uint value
    function applyDeltaToUint(bytes32 key, uint256 value) external onlyController returns (uint256) {
        uint256 currValue = uintValues[key];
        uint256 nextUint = currValue + value;
        uintValues[key] = nextUint;
        return nextUint;
    }

    // @dev add the input int value to the existing uint value, prevent the uint
    // value from becoming negative
    // @param key the key of the value
    // @param value the input int value
    // @return the new uint value
    function applyBoundedDeltaToUint(bytes32 key, int256 value) external onlyController returns (uint256) {
        uint256 uintValue = uintValues[key];
        if (value < 0 && (-value).toUint256() > uintValue) {
            uintValues[key] = 0;
            return 0;
        }

        uint256 nextUint = Calc.sumReturnUint256(uintValue, value);
        uintValues[key] = nextUint;
        return nextUint;
    }

    // @dev add the input uint value to the existing uint value
    // @param key the key of the value
    // @param value the input uint value
    // @return the new uint value
    function incrementUint(bytes32 key, uint256 value) external onlyController returns (uint256) {
        uint256 nextUint = uintValues[key] + value;
        uintValues[key] = nextUint;
        return nextUint;
    }

    // @dev subtract the input uint value from the existing uint value
    // @param key the key of the value
    // @param value the input uint value
    // @return the new uint value
    function decrementUint(bytes32 key, uint256 value) external onlyController returns (uint256) {
        uint256 nextUint = uintValues[key] - value;
        uintValues[key] = nextUint;
        return nextUint;
    }

    // @dev get the int value for the given key
    // @param key the key of the value
    // @return the int value for the key
    function getInt(bytes32 key) external view returns (int256) {
        return intValues[key];
    }

    // @dev set the int value for the given key
    // @param key the key of the value
    // @param value the value to set
    // @return the int value for the key
    function setInt(bytes32 key, int256 value) external onlyController returns (int256) {
        intValues[key] = value;
        return value;
    }

    function removeInt(bytes32 key) external onlyController {
        delete intValues[key];
    }

    // @dev add the input int value to the existing int value
    // @param key the key of the value
    // @param value the input int value
    // @return the new int value
    function applyDeltaToInt(bytes32 key, int256 value) external onlyController returns (int256) {
        int256 nextInt = intValues[key] + value;
        intValues[key] = nextInt;
        return nextInt;
    }

    // @dev add the input int value to the existing int value
    // @param key the key of the value
    // @param value the input int value
    // @return the new int value
    function incrementInt(bytes32 key, int256 value) external onlyController returns (int256) {
        int256 nextInt = intValues[key] + value;
        intValues[key] = nextInt;
        return nextInt;
    }

    // @dev subtract the input int value from the existing int value
    // @param key the key of the value
    // @param value the input int value
    // @return the new int value
    function decrementInt(bytes32 key, int256 value) external onlyController returns (int256) {
        int256 nextInt = intValues[key] - value;
        intValues[key] = nextInt;
        return nextInt;
    }

    // @dev get the address value for the given key
    // @param key the key of the value
    // @return the address value for the key
