// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "./BaseOrderHandler.sol";
import "../adl/AdlUtils.sol";
import "../order/ExecuteOrderUtils.sol";

// @title AdlHandler
// @dev Contract to handle adls
contract AdlHandler is BaseOrderHandler {
    using SafeCast for uint256;
    using SafeCast for int256;
    using Order for Order.Props;
    using Array for uint256[];

    // @dev ExecuteAdlCache struct used in executeAdl to avoid
    // stack too deep errors
    struct ExecuteAdlCache {
        uint256 startingGas;
        bytes32 key;
        bool shouldAllowAdl;
        uint256 maxPnlFactorForAdl;
        int256 pnlToPoolFactor;
        int256 nextPnlToPoolFactor;
        uint256 minPnlFactorForAdl;
    }

    IOrderExecutor public immutable decreaseOrderExecutor;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        OrderVault _orderVault,
        ISwapHandler _swapHandler,
        IReferralStorage _referralStorage,
        IOrderExecutor _decreaseOrderExecutor
    ) BaseOrderHandler(
        _roleStore,
        _dataStore,
        _eventEmitter,
        _oracle,
        MultichainVault(_multichainVault),
        _orderVault,
        _swapHandler,
        _referralStorage
    ) {
        decreaseOrderExecutor = _decreaseOrderExecutor;
    }

    // @dev checks the ADL state to update the isAdlEnabled flag
    // @param market the market to check
    // @param isLong whether to check the long or short side
    // @param oracleParams OracleUtils.SetPricesParams
    function updateAdlState(
        address market,
        bool isLong,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external
        globalNonReentrant
        onlyAdlKeeper
        withOraclePrices(oracleParams)
    {
        AdlUtils.updateAdlState(
            dataStore,
            eventEmitter,
            oracle,
            market,
            isLong
        );
    }

    // @dev auto-deleverages a position
    // there is no validation that ADL is executed in order of position profit
    // or position size, this is due to the limitation of the gas overhead
    // required to check this ordering
    //
    // ADL keepers could be separately incentivised using a rebate based on
    // position profit, this is not implemented within the contracts at the moment
    //
    // @param account the position's account
    // @param market the position's market
    // @param collateralToken the position's collateralToken
    // @param isLong whether the position is long or short
    // @param sizeDeltaUsd the size to reduce the position by
    // @param oracleParams OracleUtils.SetPricesParams
    function executeAdl(
        address account,
        address market,
        address collateralToken,
        bool isLong,
        uint256 sizeDeltaUsd,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external
        globalNonReentrant
        onlyAdlKeeper
        withOraclePrices(oracleParams)
    {
        ExecuteAdlCache memory cache;

        cache.startingGas = gasleft();

        AdlUtils.validateAdl(
            dataStore,
            oracle,
            market,
            isLong
        );

        (cache.shouldAllowAdl, cache.pnlToPoolFactor, cache.maxPnlFactorForAdl) = MarketUtils.isPnlFactorExceeded(
            dataStore,
            oracle,
            market,
            isLong,
            Keys.MAX_PNL_FACTOR_FOR_ADL
        );

        if (!cache.shouldAllowAdl) {
            revert Errors.AdlNotRequired(cache.pnlToPoolFactor, cache.maxPnlFactorForAdl);
        }

        cache.key = AdlUtils.createAdlOrder(
            AdlUtils.CreateAdlOrderParams(
                dataStore,
                eventEmitter,
                account,
                market,
                collateralToken,
                isLong,
                sizeDeltaUsd,
                oracle.minTimestamp() // updatedAtTime
            )
        );

        Order.Props memory order = OrderStoreUtils.get(dataStore, cache.key);

        BaseOrderUtils.ExecuteOrderParams memory params = _getExecuteOrderParams(
            cache.key,
            order,
            msg.sender,
            cache.startingGas,
            Order.SecondaryOrderType.Adl
        );

        FeatureUtils.validateFeature(params.contracts.dataStore, Keys.executeAdlFeatureDisabledKey(address(this), uint256(params.order.orderType())));

        ExecuteOrderUtils.executeOrder(decreaseOrderExecutor, params);

        // validate that the ratio of pending pnl to pool value was decreased
        cache.nextPnlToPoolFactor = MarketUtils.getPnlToPoolFactor(dataStore, oracle, market, isLong, true);
        if (cache.nextPnlToPoolFactor >= cache.pnlToPoolFactor) {
            revert Errors.InvalidAdl(cache.nextPnlToPoolFactor, cache.pnlToPoolFactor);
        }

        cache.minPnlFactorForAdl = MarketUtils.getMinPnlFactorAfterAdl(dataStore, market, isLong);

        if (cache.nextPnlToPoolFactor < cache.minPnlFactorForAdl.toInt256()) {
            revert Errors.PnlOvercorrected(cache.nextPnlToPoolFactor, cache.minPnlFactorForAdl);
        }
    }
}

contracts/exchange/BaseHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../event/EventEmitter.sol";
import "../oracle/IOracle.sol";
import "../oracle/OracleModule.sol";
import "../role/RoleModule.sol";
import "../utils/GlobalReentrancyGuard.sol";
import "../error/ErrorUtils.sol";
import "../feature/FeatureUtils.sol";
import "../chain/Chain.sol";

contract BaseHandler is RoleModule, GlobalReentrancyGuard, OracleModule {
    EventEmitter public immutable eventEmitter;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle
    ) RoleModule(_roleStore) GlobalReentrancyGuard(_dataStore) OracleModule(_oracle) {
        eventEmitter = _eventEmitter;
    }

    receive() external payable {
        address wnt = dataStore.getAddress(Keys.WNT);
        if (msg.sender != wnt) {
            revert Errors.InvalidNativeTokenSender(msg.sender);
        }
    }

    function validateRequestCancellation(
        uint256 createdAtTime,
        string memory requestType
    ) internal view {
        uint256 requestExpirationTime = dataStore.getUint(Keys.REQUEST_EXPIRATION_TIME);
        uint256 requestAge = Chain.currentTimestamp() - createdAtTime;
        if (requestAge < requestExpirationTime) {
            revert Errors.RequestNotYetCancellable(requestAge, requestExpirationTime, requestType);
        }
    }

    function validateNonKeeperError(bytes4 errorSelector, bytes memory reasonBytes) internal pure {
        if (
            OracleUtils.isOracleError(errorSelector) ||
            errorSelector == Errors.DisabledFeature.selector ||
            errorSelector == Errors.InsufficientGasLeftForCallback.selector ||
            errorSelector == Errors.InsufficientGasForCancellation.selector
        ) {
            ErrorUtils.revertWithCustomError(reasonBytes);
        }
    }

    function validateDataListLength(uint256 dataLength) internal view {
        uint256 maxDataLength = dataStore.getUint(Keys.MAX_DATA_LENGTH);
        if (dataLength > maxDataLength) {
            revert Errors.MaxDataListLengthExceeded(dataLength, maxDataLength);
        }
    }
}

contracts/exchange/BaseOrderHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "./BaseHandler.sol";

import "../market/MarketUtils.sol";
import "../order/BaseOrderUtils.sol";
import "../order/OrderVault.sol";
import "../order/Order.sol";
import "../swap/ISwapHandler.sol";

import "../referral/IReferralStorage.sol";

// @title BaseOrderHandler
// @dev Base contract for shared order handler functions
contract BaseOrderHandler is BaseHandler {
    using SafeCast for uint256;
    using Order for Order.Props;
    using Array for uint256[];

    MultichainVault public immutable multichainVault;
    OrderVault public immutable orderVault;
    ISwapHandler public immutable swapHandler;
    IReferralStorage public immutable referralStorage;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        OrderVault _orderVault,
        ISwapHandler _swapHandler,
        IReferralStorage _referralStorage
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        orderVault = _orderVault;
        swapHandler = _swapHandler;
        referralStorage = _referralStorage;
    }

    // @dev get the BaseOrderUtils.ExecuteOrderParams to execute an order
    // @return the required BaseOrderUtils.ExecuteOrderParams params to execute the order
    function _getExecuteOrderParams(
        bytes32 key,
        Order.Props memory order,
        address keeper,
        uint256 startingGas,
        Order.SecondaryOrderType secondaryOrderType
    ) internal view returns (BaseOrderUtils.ExecuteOrderParams memory) {
        BaseOrderUtils.ExecuteOrderParams memory params;

        params.key = key;
        params.order = order;
        params.swapPathMarkets = MarketUtils.getSwapPathMarkets(
            dataStore,
            params.order.swapPath()
        );

        params.contracts.dataStore = dataStore;
        params.contracts.eventEmitter = eventEmitter;
        params.contracts.multichainVault = multichainVault;
        params.contracts.orderVault = orderVault;
        params.contracts.oracle = oracle;
        params.contracts.swapHandler = swapHandler;
        params.contracts.referralStorage = referralStorage;

        params.minOracleTimestamp = oracle.minTimestamp();
        params.maxOracleTimestamp = oracle.maxTimestamp();

        if (params.order.market() != address(0)) {
            params.market = MarketUtils.getEnabledMarket(params.contracts.dataStore, params.order.market());
        }

        params.keeper = keeper;
        params.startingGas = startingGas;

        params.secondaryOrderType = secondaryOrderType;

        return params;
    }
}

contracts/exchange/DepositHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "./BaseHandler.sol";

import "../market/Market.sol";

import "../deposit/Deposit.sol";
import "../deposit/DepositVault.sol";
import "../deposit/DepositUtils.sol";
import "../deposit/ExecuteDepositUtils.sol";

import "../multichain/MultichainVault.sol";
import "../multichain/IMultichainTransferRouter.sol";

import "./IDepositHandler.sol";

// @title DepositHandler
// @dev Contract to handle creation, execution and cancellation of deposits
contract DepositHandler is IDepositHandler, BaseHandler {
    using Deposit for Deposit.Props;

    DepositVault public immutable depositVault;
    MultichainVault public immutable multichainVault;
    IMultichainTransferRouter public immutable multichainTransferRouter;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        IMultichainTransferRouter _multichainTransferRouter,
        DepositVault _depositVault,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        multichainTransferRouter = _multichainTransferRouter;
        depositVault = _depositVault;
        swapHandler = _swapHandler;
    }

    // @dev creates a deposit in the deposit store
    // @param account the depositing account
    // @param srcChainId the source chain id
    // @param params IDepositUtils.CreateDepositParams
    function createDeposit(
        address account,
        uint256 srcChainId,
        IDepositUtils.CreateDepositParams calldata params
    ) external override globalNonReentrant onlyController returns (bytes32) {
        FeatureUtils.validateFeature(dataStore, Keys.createDepositFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        return DepositUtils.createDeposit(
            dataStore,
            eventEmitter,
            depositVault,
            account,
            srcChainId,
            params
        );
    }

    // @dev cancels a deposit
    // @param key the deposit key
    function cancelDeposit(bytes32 key) external override globalNonReentrant onlyController {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;
        Deposit.Props memory deposit = DepositStoreUtils.get(_dataStore, key);

        FeatureUtils.validateFeature(_dataStore, Keys.cancelDepositFeatureDisabledKey(address(this)));

        validateRequestCancellation(
            deposit.updatedAtTime(),
            "Deposit"
        );

        DepositUtils.cancelDeposit(
            _dataStore,
            eventEmitter,
            multichainVault,
            depositVault,
            key,
            deposit.account(),
            startingGas,
            Keys.USER_INITIATED_CANCEL,
            ""
        );
    }

    // @dev executes a deposit
    // @param key the key of the deposit to execute
    // @param oracleParams OracleUtils.SetPricesParams
    function executeDeposit(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external
        globalNonReentrant
        onlyOrderKeeper
        withOraclePrices(oracleParams)
    {
        uint256 startingGas = gasleft();

        Deposit.Props memory deposit = DepositStoreUtils.get(dataStore, key);
        uint256 estimatedGasLimit = GasUtils.estimateExecuteDepositGasLimit(dataStore, deposit);
        GasUtils.validateExecutionGas(dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(dataStore, startingGas);

        try this._executeDeposit{ gas: executionGas }(
            key,
            deposit,
            msg.sender
        ) {
        } catch (bytes memory reasonBytes) {
            _handleDepositError(
                key,
                startingGas,
                reasonBytes
            );
        }
    }

    function executeDepositFromController(
        IExecuteDepositUtils.ExecuteDepositParams calldata executeDepositParams,
        Deposit.Props calldata deposit
    ) external onlyController returns (uint256) {
        return ExecuteDepositUtils.executeDeposit(executeDepositParams, deposit);
    }

    // @dev simulate execution of a deposit to check for any errors
    // @param key the deposit key
    // @param params OracleUtils.SimulatePricesParams
    function simulateExecuteDeposit(
        bytes32 key,
        OracleUtils.SimulatePricesParams memory params
    ) external
        override
        onlyController
        withSimulatedOraclePrices(params)
        globalNonReentrant
    {
        Deposit.Props memory deposit = DepositStoreUtils.get(dataStore, key);

        this._executeDeposit(
            key,
            deposit,
            msg.sender
        );
    }

    // @dev executes a deposit
    // @param oracleParams OracleUtils.SetPricesParams
    // @param keeper the keeper executing the deposit
    // @param startingGas the starting gas
    function _executeDeposit(
        bytes32 key,
        Deposit.Props memory deposit,
        address keeper
    ) external onlySelf {
        uint256 startingGas = gasleft();

        FeatureUtils.validateFeature(dataStore, Keys.executeDepositFeatureDisabledKey(address(this)));

        IExecuteDepositUtils.ExecuteDepositParams memory params = IExecuteDepositUtils.ExecuteDepositParams(
            dataStore,
            eventEmitter,
            multichainVault,
            multichainTransferRouter,
            depositVault,
            oracle,
            swapHandler,
            key,
            keeper,
            startingGas,
            ISwapPricingUtils.SwapPricingType.Deposit,
            true, // includeVirtualInventoryImpact
            deposit.srcChainId()
        );

        ExecuteDepositUtils.executeDeposit(params, deposit);
    }

    // @dev handle errors from deposits
    // @param key the deposit key
    // @param startingGas the starting gas of the txn
    // @param reasonBytes the reason bytes of the error
    function _handleDepositError(
        bytes32 key,
        uint256 startingGas,
        bytes memory reasonBytes
    ) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason, /* bool hasRevertMessage */) = ErrorUtils.getRevertMessage(reasonBytes);

        DepositUtils.cancelDeposit(
            dataStore,
            eventEmitter,
            multichainVault,
            depositVault,
            key,
            msg.sender,
            startingGas,
            reason,
            reasonBytes
        );
    }
}

contracts/exchange/GlvDepositHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./IGlvDepositHandler.sol";
import "./BaseHandler.sol";

import "../glv/glvDeposit/GlvDepositUtils.sol";
import "../glv/glvDeposit/ExecuteGlvDepositUtils.sol";

contract GlvDepositHandler is IGlvDepositHandler, BaseHandler, ReentrancyGuard {
    using GlvDeposit for GlvDeposit.Props;

    MultichainVault public immutable multichainVault;
    IMultichainTransferRouter public immutable multichainTransferRouter;
    GlvVault public immutable glvVault;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        IMultichainTransferRouter _multichainTransferRouter,
        GlvVault _glvVault,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        multichainTransferRouter = _multichainTransferRouter;
        glvVault = _glvVault;
        swapHandler = _swapHandler;
    }

    function createGlvDeposit(
        address account,
        uint256 srcChainId,
        IGlvDepositUtils.CreateGlvDepositParams calldata params
    ) external globalNonReentrant onlyController returns (bytes32) {
        FeatureUtils.validateFeature(dataStore, Keys.createGlvDepositFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        return GlvDepositUtils.createGlvDeposit(dataStore, eventEmitter, glvVault, account, srcChainId, params);
    }

    // @key glvDeposit key
    // @oracleParams prices for all markets in GLV are required
    function executeGlvDeposit(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external globalNonReentrant onlyOrderKeeper withOraclePrices(oracleParams) {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;

        GlvDeposit.Props memory glvDeposit = GlvDepositStoreUtils.get(_dataStore, key);
        uint256 marketCount = GlvUtils.getGlvMarketCount(_dataStore, glvDeposit.glv());
        uint256 estimatedGasLimit = GasUtils.estimateExecuteGlvDepositGasLimit(_dataStore, glvDeposit, marketCount);
        GasUtils.validateExecutionGas(_dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(_dataStore, startingGas);

        try this._executeGlvDeposit{gas: executionGas}(key, glvDeposit, msg.sender) {} catch (
            bytes memory reasonBytes
        ) {
            _handleGlvDepositError(key, startingGas, reasonBytes);
        }
    }

    function _executeGlvDeposit(bytes32 key, GlvDeposit.Props memory glvDeposit, address keeper) external onlySelf {
        uint256 startingGas = gasleft();

        FeatureUtils.validateFeature(dataStore, Keys.executeGlvDepositFeatureDisabledKey(address(this)));

        ExecuteGlvDepositUtils.ExecuteGlvDepositParams memory params = ExecuteGlvDepositUtils.ExecuteGlvDepositParams({
            key: key,
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            multichainTransferRouter: multichainTransferRouter,
            glvVault: glvVault,
            oracle: oracle,
            swapHandler: swapHandler,
            startingGas: startingGas,
            keeper: keeper
        });

        ExecuteGlvDepositUtils.executeGlvDeposit(params, glvDeposit);
    }

    function _handleGlvDepositError(bytes32 key, uint256 startingGas, bytes memory reasonBytes) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason /* bool hasRevertMessage */, ) = ErrorUtils.getRevertMessage(reasonBytes);

        GlvDepositUtils.CancelGlvDepositParams memory params = GlvDepositUtils.CancelGlvDepositParams({
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            glvVault: glvVault,
            key: key,
            keeper: msg.sender,
            startingGas: startingGas,
            reason: reason,
            reasonBytes: reasonBytes
        });
        GlvDepositUtils.cancelGlvDeposit(params);
    }

    function cancelGlvDeposit(bytes32 key) external globalNonReentrant onlyController {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;
        FeatureUtils.validateFeature(_dataStore, Keys.cancelGlvDepositFeatureDisabledKey(address(this)));

        GlvDeposit.Props memory glvDeposit = GlvDepositStoreUtils.get(_dataStore, key);
        validateRequestCancellation(glvDeposit.updatedAtTime(), "GlvDeposit");

        GlvDepositUtils.CancelGlvDepositParams memory params = GlvDepositUtils.CancelGlvDepositParams({
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            glvVault: glvVault,
            key: key,
            keeper: glvDeposit.account(),
            startingGas: startingGas,
            reason: Keys.USER_INITIATED_CANCEL,
            reasonBytes: ""
        });
        GlvDepositUtils.cancelGlvDeposit(params);
    }

    function simulateExecuteGlvDeposit(
        bytes32 key,
        OracleUtils.SimulatePricesParams memory params
    ) external onlyController withSimulatedOraclePrices(params) globalNonReentrant {
        GlvDeposit.Props memory glvDeposit = GlvDepositStoreUtils.get(dataStore, key);

        this._executeGlvDeposit(key, glvDeposit, msg.sender);
    }
}

contracts/exchange/GlvShiftHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./BaseHandler.sol";

import "../glv/glvShift/GlvShiftUtils.sol";

contract GlvShiftHandler is BaseHandler, ReentrancyGuard {
    using GlvShift for GlvShift.Props;

    MultichainVault public immutable multichainVault;
    GlvVault public immutable glvVault;
    ShiftVault public immutable shiftVault;
    IDepositHandler public immutable depositHandler;
    IWithdrawalHandler public immutable withdrawalHandler;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        GlvVault _glvVault,
        ShiftVault _shiftVault,
        IDepositHandler _depositHandler,
        IWithdrawalHandler _withdrawalHandler,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        glvVault = _glvVault;
        shiftVault = _shiftVault;
        depositHandler = _depositHandler;
        withdrawalHandler = _withdrawalHandler;
        swapHandler = _swapHandler;
    }

    function createGlvShift(
        GlvShiftUtils.CreateGlvShiftParams memory params
    ) external globalNonReentrant onlyOrderKeeper returns (bytes32) {
        FeatureUtils.validateFeature(dataStore, Keys.createGlvShiftFeatureDisabledKey(address(this)));

        return GlvShiftUtils.createGlvShift(dataStore, eventEmitter, params);
    }

    // @key glvDeposit key
    // @oracleParams prices for `fromMarket` and `toMarket` are required
    function executeGlvShift(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external globalNonReentrant onlyOrderKeeper withOraclePrices(oracleParams) {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;

        GlvShift.Props memory glvShift = GlvShiftStoreUtils.get(_dataStore, key);
        uint256 estimatedGasLimit = GasUtils.estimateExecuteGlvShiftGasLimit(_dataStore);
        GasUtils.validateExecutionGas(_dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(_dataStore, startingGas);

        try this._executeGlvShift{gas: executionGas}(key, glvShift, msg.sender) {} catch (bytes memory reasonBytes) {
            _handleGlvShiftError(key, reasonBytes);
        }
    }

    function _executeGlvShift(bytes32 key, GlvShift.Props memory glvShift, address keeper) external onlySelf {
        FeatureUtils.validateFeature(dataStore, Keys.executeGlvShiftFeatureDisabledKey(address(this)));

        GlvShiftUtils.ExecuteGlvShiftParams memory params = GlvShiftUtils.ExecuteGlvShiftParams({
            key: key,
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            shiftVault: shiftVault,
            glvVault: glvVault,
            depositHandler: depositHandler,
            withdrawalHandler: withdrawalHandler,
            swapHandler: swapHandler,
            oracle: oracle,
            keeper: keeper
        });

        GlvShiftUtils.executeGlvShift(params, glvShift);
    }

    function _handleGlvShiftError(bytes32 key, bytes memory reasonBytes) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason /* bool hasRevertMessage */, ) = ErrorUtils.getRevertMessage(reasonBytes);

        GlvShiftUtils.cancelGlvShift(
            dataStore,
            eventEmitter,
            key,
            reason,
            reasonBytes
        );
    }

    function addMarketToGlv(address glv, address market) external globalNonReentrant onlyConfigKeeper {
        GlvUtils.addMarketToGlv(dataStore, eventEmitter, glv, market);
    }

    function removeMarketFromGlv(address glv, address market) external globalNonReentrant onlyConfigKeeper {
        GlvUtils.removeMarketFromGlv(dataStore, eventEmitter, glv, market);
    }
}

contracts/exchange/GlvWithdrawalHandler..content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./IGlvWithdrawalHandler.sol";
import "./BaseHandler.sol";

import "../glv/glvWithdrawal/GlvWithdrawalUtils.sol";
import "../multichain/IMultichainTransferRouter.sol";

contract GlvWithdrawalHandler is IGlvWithdrawalHandler, BaseHandler, ReentrancyGuard {
    using GlvWithdrawal for GlvWithdrawal.Props;

    MultichainVault public immutable multichainVault;
    IMultichainTransferRouter public immutable multichainTransferRouter;
    GlvVault public immutable glvVault;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        IMultichainTransferRouter _multichainTransferRouter,
        GlvVault _glvVault,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        multichainTransferRouter = _multichainTransferRouter;
        glvVault = _glvVault;
        swapHandler = _swapHandler;
    }

    function createGlvWithdrawal(
        address account,
        uint256 srcChainId,
        IGlvWithdrawalUtils.CreateGlvWithdrawalParams calldata params
    ) external globalNonReentrant onlyController returns (bytes32) {
        DataStore _dataStore = dataStore;
        FeatureUtils.validateFeature(_dataStore, Keys.createGlvWithdrawalFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        return GlvWithdrawalUtils.createGlvWithdrawal(_dataStore, eventEmitter, glvVault, account, srcChainId, params);
    }

    // @key glvDeposit key
    // @oracleParams prices for all markets in GLV are required
    function executeGlvWithdrawal(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external globalNonReentrant onlyOrderKeeper withOraclePrices(oracleParams) {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;

        GlvWithdrawal.Props memory glvWithdrawal = GlvWithdrawalStoreUtils.get(_dataStore, key);
        uint256 marketCount = GlvUtils.getGlvMarketCount(_dataStore, glvWithdrawal.glv());
        uint256 estimatedGasLimit = GasUtils.estimateExecuteGlvWithdrawalGasLimit(
            _dataStore,
            glvWithdrawal,
            marketCount
        );
        GasUtils.validateExecutionGas(_dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(_dataStore, startingGas);

        try this._executeGlvWithdrawal{gas: executionGas}(key, glvWithdrawal, msg.sender) {} catch (
            bytes memory reasonBytes
        ) {
            _handleGlvWithdrawalError(key, startingGas, reasonBytes);
        }
    }

    function _executeGlvWithdrawal(
        bytes32 key,
        GlvWithdrawal.Props memory glvWithdrawal,
        address keeper
    ) external onlySelf {
        uint256 startingGas = gasleft();

        FeatureUtils.validateFeature(dataStore, Keys.executeGlvWithdrawalFeatureDisabledKey(address(this)));

        GlvWithdrawalUtils.ExecuteGlvWithdrawalParams memory params = GlvWithdrawalUtils.ExecuteGlvWithdrawalParams({
            key: key,
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            glvVault: glvVault,
            oracle: oracle,
            swapHandler: swapHandler,
            startingGas: startingGas,
            keeper: keeper
        });

        GlvWithdrawalUtils.executeGlvWithdrawal(params, glvWithdrawal);
    }

    function _handleGlvWithdrawalError(bytes32 key, uint256 startingGas, bytes memory reasonBytes) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason /* bool hasRevertMessage */, ) = ErrorUtils.getRevertMessage(reasonBytes);

        GlvWithdrawalUtils.CancelGlvWithdrawalParams memory params = GlvWithdrawalUtils.CancelGlvWithdrawalParams({
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            glvVault: glvVault,
            key: key,
            keeper: msg.sender,
            startingGas: startingGas,
            reason: reason,
            reasonBytes: reasonBytes
        });
        GlvWithdrawalUtils.cancelGlvWithdrawal(params);
    }

    function cancelGlvWithdrawal(bytes32 key) external globalNonReentrant onlyController {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;
        FeatureUtils.validateFeature(_dataStore, Keys.cancelGlvWithdrawalFeatureDisabledKey(address(this)));

        GlvWithdrawal.Props memory glvWithdrawal = GlvWithdrawalStoreUtils.get(_dataStore, key);
        validateRequestCancellation(glvWithdrawal.updatedAtTime(), "GlvWithdrawal");

        GlvWithdrawalUtils.CancelGlvWithdrawalParams memory params = GlvWithdrawalUtils.CancelGlvWithdrawalParams({
            dataStore: dataStore,
            eventEmitter: eventEmitter,
            multichainVault: multichainVault,
            glvVault: glvVault,
            key: key,
            keeper: glvWithdrawal.account(),
            startingGas: startingGas,
            reason: Keys.USER_INITIATED_CANCEL,
            reasonBytes: ""
        });
        GlvWithdrawalUtils.cancelGlvWithdrawal(params);
    }

    function simulateExecuteGlvWithdrawal(
        bytes32 key,
        OracleUtils.SimulatePricesParams memory params
    ) external onlyController withSimulatedOraclePrices(params) globalNonReentrant {
        GlvWithdrawal.Props memory glvWithdrawal = GlvWithdrawalStoreUtils.get(dataStore, key);

        this._executeGlvWithdrawal(key, glvWithdrawal, msg.sender);
    }
}

