// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../exchange/IWithdrawalHandler.sol";

import "../shift/Shift.sol";
import "../shift/ShiftVault.sol";
import "../shift/ShiftUtils.sol";

import "./IShiftHandler.sol";
import "./BaseHandler.sol";

contract ShiftHandler is IShiftHandler, BaseHandler {
    using Shift for Shift.Props;

    MultichainVault public immutable multichainVault;
    ShiftVault public immutable shiftVault;
    IDepositHandler public depositHandler;
    IWithdrawalHandler public withdrawalHandler;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        ShiftVault _shiftVault,
        IDepositHandler _depositHandler,
        IWithdrawalHandler _withdrawalHandler,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        shiftVault = _shiftVault;
        depositHandler = _depositHandler;
        withdrawalHandler = _withdrawalHandler;
        swapHandler = _swapHandler;
    }

    function createShift(
        address account,
        uint256 srcChainId,
        IShiftUtils.CreateShiftParams calldata params
    ) external override globalNonReentrant onlyController returns (bytes32) {
        FeatureUtils.validateFeature(dataStore, Keys.createShiftFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        return ShiftUtils.createShift(
            dataStore,
            eventEmitter,
            shiftVault,
            account,
            srcChainId,
            params
        );
    }

    function cancelShift(bytes32 key) external override globalNonReentrant onlyController {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;
        Shift.Props memory shift = ShiftStoreUtils.get(_dataStore, key);

        FeatureUtils.validateFeature(_dataStore, Keys.cancelShiftFeatureDisabledKey(address(this)));

        validateRequestCancellation(
            shift.updatedAtTime(),
            "Shift"
        );

        ShiftUtils.cancelShift(
            _dataStore,
            eventEmitter,
            multichainVault,
            shiftVault,
            key,
            shift.account(),
            startingGas,
            Keys.USER_INITIATED_CANCEL,
            ""
        );
    }

    function executeShift(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    ) external
        globalNonReentrant
        onlyOrderKeeper
        withOraclePrices(oracleParams)
    {
        uint256 startingGas = gasleft();

        Shift.Props memory shift = ShiftStoreUtils.get(dataStore, key);
        uint256 estimatedGasLimit = GasUtils.estimateExecuteShiftGasLimit(dataStore, shift);
        GasUtils.validateExecutionGas(dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(dataStore, startingGas);

        try this._executeShift{ gas: executionGas }(
            key,
            shift,
            msg.sender
        ) {
        } catch (bytes memory reasonBytes) {
            _handleShiftError(
                key,
                startingGas,
                reasonBytes
            );
        }
    }

    function simulateExecuteShift(
        bytes32 key,
        OracleUtils.SimulatePricesParams memory params
    ) external
        override
        onlyController
        withSimulatedOraclePrices(params)
        globalNonReentrant
    {
        Shift.Props memory shift = ShiftStoreUtils.get(dataStore, key);

        this._executeShift(
            key,
            shift,
            msg.sender
        );
    }

    function _executeShift(
        bytes32 key,
        Shift.Props memory shift,
        address keeper
    ) external onlySelf {
        uint256 startingGas = gasleft();

        FeatureUtils.validateFeature(dataStore, Keys.executeShiftFeatureDisabledKey(address(this)));

        ShiftUtils.ExecuteShiftParams memory params = ShiftUtils.ExecuteShiftParams(
            dataStore,
            eventEmitter,
            multichainVault,
            shiftVault,
            oracle,
            depositHandler,
            withdrawalHandler,
            swapHandler,
            key,
            keeper,
            startingGas
        );

        ShiftUtils.executeShift(params, shift);
    }

    function _handleShiftError(
        bytes32 key,
        uint256 startingGas,
        bytes memory reasonBytes
    ) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason, /* bool hasRevertMessage */) = ErrorUtils.getRevertMessage(reasonBytes);

        ShiftUtils.cancelShift(
            dataStore,
            eventEmitter,
            multichainVault,
            shiftVault,
            key,
            msg.sender,
            startingGas,
            reason,
            reasonBytes
        );
    }
}

contracts/exchange/WithdrawalHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "./BaseHandler.sol";
import "../error/ErrorUtils.sol";

import "../market/Market.sol";

import "../withdrawal/Withdrawal.sol";
import "../withdrawal/WithdrawalVault.sol";
import "../withdrawal/WithdrawalStoreUtils.sol";
import "../withdrawal/WithdrawalUtils.sol";
import "../withdrawal/ExecuteWithdrawalUtils.sol";

import "./IWithdrawalHandler.sol";

// @title WithdrawalHandler
// @dev Contract to handle creation, execution and cancellation of withdrawals
contract WithdrawalHandler is IWithdrawalHandler, BaseHandler {
    using Withdrawal for Withdrawal.Props;

    MultichainVault public immutable multichainVault;
    WithdrawalVault public immutable withdrawalVault;
    ISwapHandler public immutable swapHandler;

    constructor(
        RoleStore _roleStore,
        DataStore _dataStore,
        EventEmitter _eventEmitter,
        IOracle _oracle,
        MultichainVault _multichainVault,
        WithdrawalVault _withdrawalVault,
        ISwapHandler _swapHandler
    ) BaseHandler(_roleStore, _dataStore, _eventEmitter, _oracle) {
        multichainVault = _multichainVault;
        withdrawalVault = _withdrawalVault;
        swapHandler = _swapHandler;
    }

    // @dev creates a withdrawal in the withdrawal store
    // @param account the withdrawing account
    // @param srcChainId the source chain id
    // @param params IWithdrawalUtils.CreateWithdrawalParams
    function createWithdrawal(
        address account,
        uint256 srcChainId,
        IWithdrawalUtils.CreateWithdrawalParams calldata params
    ) external override globalNonReentrant onlyController returns (bytes32) {
        FeatureUtils.validateFeature(dataStore, Keys.createWithdrawalFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        return WithdrawalUtils.createWithdrawal(
            dataStore,
            eventEmitter,
            withdrawalVault,
            account,
            srcChainId,
            params,
            false // isAtomicWithdrawal
        );
    }

    // @dev cancels a withdrawal
    // @param key the withdrawal key
    function cancelWithdrawal(bytes32 key) external override globalNonReentrant onlyController {
        uint256 startingGas = gasleft();

        DataStore _dataStore = dataStore;
        Withdrawal.Props memory withdrawal = WithdrawalStoreUtils.get(_dataStore, key);

        FeatureUtils.validateFeature(_dataStore, Keys.cancelWithdrawalFeatureDisabledKey(address(this)));

        validateRequestCancellation(
            withdrawal.updatedAtTime(),
            "Withdrawal"
        );

        WithdrawalUtils.cancelWithdrawal(
            _dataStore,
            eventEmitter,
            multichainVault,
            withdrawalVault,
            key,
            withdrawal.account(),
            startingGas,
            Keys.USER_INITIATED_CANCEL,
            ""
        );
    }

    // @dev executes a withdrawal
    // @param key the key of the withdrawal to execute
    // @param oracleParams OracleUtils.SetPricesParams
    function executeWithdrawal(
        bytes32 key,
        OracleUtils.SetPricesParams calldata oracleParams
    )
        external
        globalNonReentrant
        onlyOrderKeeper
        withOraclePrices(oracleParams)
    {
        uint256 startingGas = gasleft();

        oracle.validateSequencerUp();

        Withdrawal.Props memory withdrawal = WithdrawalStoreUtils.get(dataStore, key);
        uint256 estimatedGasLimit = GasUtils.estimateExecuteWithdrawalGasLimit(dataStore, withdrawal);
        GasUtils.validateExecutionGas(dataStore, startingGas, estimatedGasLimit);

        uint256 executionGas = GasUtils.getExecutionGas(dataStore, startingGas);

        try this._executeWithdrawal{ gas: executionGas }(
            key,
            withdrawal,
            msg.sender,
            ISwapPricingUtils.SwapPricingType.Withdrawal
        ) {
        } catch (bytes memory reasonBytes) {
            _handleWithdrawalError(
                key,
                startingGas,
                reasonBytes
            );
        }
    }

    function executeWithdrawalFromController(
        IExecuteWithdrawalUtils.ExecuteWithdrawalParams calldata executeWithdrawalParams,
        Withdrawal.Props calldata withdrawal
    ) external onlyController returns (IExecuteWithdrawalUtils.ExecuteWithdrawalResult memory) {
        return ExecuteWithdrawalUtils.executeWithdrawal(executeWithdrawalParams, withdrawal);
    }

    // @notice this function can only be called for markets where Chainlink
    // on-chain feeds are configured for all the tokens of the market
    // for example, if the market has index token as DOGE, long token as WETH
    // and short token as USDC, Chainlink on-chain feeds must be configured
    // for DOGE, WETH, USDC for this method to be callable for the market
    function executeAtomicWithdrawal(
        address account,
        IWithdrawalUtils.CreateWithdrawalParams calldata params,
        OracleUtils.SetPricesParams calldata oracleParams
    )
        external
        globalNonReentrant
        onlyController
        withOraclePricesForAtomicAction(oracleParams)
    {
        FeatureUtils.validateFeature(dataStore, Keys.executeAtomicWithdrawalFeatureDisabledKey(address(this)));
        validateDataListLength(params.dataList.length);

        if (
            params.addresses.longTokenSwapPath.length != 0 ||
            params.addresses.shortTokenSwapPath.length != 0
        ) {
            revert Errors.SwapsNotAllowedForAtomicWithdrawal(
                params.addresses.longTokenSwapPath.length,
                params.addresses.shortTokenSwapPath.length
            );
        }

        bytes32 key = WithdrawalUtils.createWithdrawal(
            dataStore,
            eventEmitter,
            withdrawalVault,
            account,
            0, // srcChainId is the current block.chainId
            params,
            true // isAtomicWithdrawal
        );

        Withdrawal.Props memory withdrawal = WithdrawalStoreUtils.get(dataStore, key);

        this._executeWithdrawal(
            key,
            withdrawal,
            account,
            ISwapPricingUtils.SwapPricingType.AtomicWithdrawal
        );
    }

    // @dev simulate execution of a withdrawal to check for any errors
    // @param key the withdrawal key
    // @param params OracleUtils.SimulatePricesParams
    function simulateExecuteWithdrawal(
        bytes32 key,
        OracleUtils.SimulatePricesParams memory params,
        ISwapPricingUtils.SwapPricingType swapPricingType
    ) external
        override
        onlyController
        withSimulatedOraclePrices(params)
        globalNonReentrant
    {
        oracle.validateSequencerUp();

        Withdrawal.Props memory withdrawal = WithdrawalStoreUtils.get(dataStore, key);

        this._executeWithdrawal(
            key,
            withdrawal,
            msg.sender,
            swapPricingType
        );
    }

    // @dev executes a withdrawal
    // @param oracleParams OracleUtils.SetPricesParams
    // @param keeper the keeper executing the withdrawal
    // @param startingGas the starting gas
    function _executeWithdrawal(
        bytes32 key,
        Withdrawal.Props memory withdrawal,
        address keeper,
        ISwapPricingUtils.SwapPricingType swapPricingType
    ) external onlySelf {
        uint256 startingGas = gasleft();

        FeatureUtils.validateFeature(dataStore, Keys.executeWithdrawalFeatureDisabledKey(address(this)));

        IExecuteWithdrawalUtils.ExecuteWithdrawalParams memory params = IExecuteWithdrawalUtils.ExecuteWithdrawalParams(
            dataStore,
            eventEmitter,
            multichainVault,
            withdrawalVault,
            oracle,
            swapHandler,
            key,
            keeper,
            startingGas,
            swapPricingType
        );

        ExecuteWithdrawalUtils.executeWithdrawal(params, withdrawal);
    }

    function _handleWithdrawalError(
        bytes32 key,
        uint256 startingGas,
        bytes memory reasonBytes
    ) internal {
        GasUtils.validateExecutionErrorGas(dataStore, reasonBytes);

        bytes4 errorSelector = ErrorUtils.getErrorSelectorFromData(reasonBytes);

        validateNonKeeperError(errorSelector, reasonBytes);

        (string memory reason, /* bool hasRevertMessage */) = ErrorUtils.getRevertMessage(reasonBytes);

        WithdrawalUtils.cancelWithdrawal(
            dataStore,
            eventEmitter,
            multichainVault,
            withdrawalVault,
            key,
            msg.sender,
            startingGas,
            reason,
            reasonBytes
        );
    }
}

contracts/external/ExternalHandler.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

import "./IExternalHandler.sol";
import "../error/Errors.sol";

// contracts with a CONTROLLER role or other roles may need to call external
// contracts, since these roles may be able to directly change DataStore values
// or perform other sensitive operations, these contracts should make these calls
// through ExternalHandler instead
//
// note that anyone can make this contract call any function, this should be noted
// to avoid assumptions of the contract's state in any protocol
//
// e.g. some tokens require the approved amount to be zero before the approved amount
// can be changed, this should be taken into account if calling approve is required for
// these tokens
contract ExternalHandler is IExternalHandler, ReentrancyGuard {
    using Address for address;
    using SafeERC20 for IERC20;

    // @notice refundTokens should be unique, this is because the refund loop
    // sends the full refund token balance on each iteration, so if there are
    // duplicate refund token addresses, then only the first refundReceiver
    // for that token would receive the tokens
    function makeExternalCalls(
        address[] memory targets,
        bytes[] memory dataList,
        address[] memory refundTokens,
        address[] memory refundReceivers
    ) external nonReentrant {
        if (targets.length != dataList.length) {
            revert Errors.InvalidExternalCallInput(targets.length, dataList.length);
        }

        if (refundTokens.length != refundReceivers.length) {
            revert Errors.InvalidExternalReceiversInput(refundTokens.length, refundReceivers.length);
        }

        for (uint256 i; i < targets.length; i++) {
            _makeExternalCall(
                targets[i],
                dataList[i]
            );
        }

        for (uint256 i; i < refundTokens.length; i++) {
            IERC20 refundToken = IERC20(refundTokens[i]);
            uint256 balance = refundToken.balanceOf(address(this));
            if (balance > 0) {
                refundToken.safeTransfer(refundReceivers[i], balance);
            }
        }
    }

    function _makeExternalCall(
        address target,
        bytes memory data
    ) internal {
        if (!target.isContract()) {
            revert Errors.InvalidExternalCallTarget(target);
        }

        (bool success, bytes memory returndata) = target.call(data);

        if (!success) {
            revert Errors.ExternalCallFailed(returndata);
        }
    }
}

