pragma solidity ^0.8.0;

import "../../data/Keys.sol";
import "../../data/DataStore.sol";

import "./GlvWithdrawal.sol";

/**
 * @title GlvWithdrawalStoreUtils
 * @dev Library for withdrawal storage functions
 */
library GlvWithdrawalStoreUtils {
    using GlvWithdrawal for GlvWithdrawal.Props;

    bytes32 public constant ACCOUNT = keccak256(abi.encode("ACCOUNT"));
    bytes32 public constant RECEIVER = keccak256(abi.encode("RECEIVER"));
    bytes32 public constant CALLBACK_CONTRACT = keccak256(abi.encode("CALLBACK_CONTRACT"));
    bytes32 public constant UI_FEE_RECEIVER = keccak256(abi.encode("UI_FEE_RECEIVER"));
    bytes32 public constant MARKET = keccak256(abi.encode("MARKET"));
    bytes32 public constant GLV = keccak256(abi.encode("GLV"));
    bytes32 public constant LONG_TOKEN_SWAP_PATH = keccak256(abi.encode("LONG_TOKEN_SWAP_PATH"));
    bytes32 public constant SHORT_TOKEN_SWAP_PATH = keccak256(abi.encode("SHORT_TOKEN_SWAP_PATH"));

    bytes32 public constant GLV_TOKEN_AMOUNT = keccak256(abi.encode("GLV_TOKEN_AMOUNT"));
    bytes32 public constant MIN_LONG_TOKEN_AMOUNT = keccak256(abi.encode("MIN_LONG_TOKEN_AMOUNT"));
    bytes32 public constant MIN_SHORT_TOKEN_AMOUNT = keccak256(abi.encode("MIN_SHORT_TOKEN_AMOUNT"));
    bytes32 public constant UPDATED_AT_TIME = keccak256(abi.encode("UPDATED_AT_TIME"));
    bytes32 public constant EXECUTION_FEE = keccak256(abi.encode("EXECUTION_FEE"));
    bytes32 public constant CALLBACK_GAS_LIMIT = keccak256(abi.encode("CALLBACK_GAS_LIMIT"));
    bytes32 public constant SRC_CHAIN_ID = keccak256(abi.encode("SRC_CHAIN_ID"));

    bytes32 public constant SHOULD_UNWRAP_NATIVE_TOKEN = keccak256(abi.encode("SHOULD_UNWRAP_NATIVE_TOKEN"));

    bytes32 public constant DATA_LIST = keccak256(abi.encode("DATA_LIST"));

    function get(DataStore dataStore, bytes32 key) external view returns (GlvWithdrawal.Props memory) {
        GlvWithdrawal.Props memory withdrawal;
        if (!dataStore.containsBytes32(Keys.GLV_WITHDRAWAL_LIST, key)) {
            return withdrawal;
        }

        withdrawal.setAccount(dataStore.getAddress(
            keccak256(abi.encode(key, ACCOUNT))
        ));

        withdrawal.setReceiver(dataStore.getAddress(
            keccak256(abi.encode(key, RECEIVER))
        ));

        withdrawal.setCallbackContract(dataStore.getAddress(
            keccak256(abi.encode(key, CALLBACK_CONTRACT))
        ));

        withdrawal.setUiFeeReceiver(dataStore.getAddress(
            keccak256(abi.encode(key, UI_FEE_RECEIVER))
        ));

        withdrawal.setMarket(dataStore.getAddress(
            keccak256(abi.encode(key, MARKET))
        ));

        withdrawal.setGlv(dataStore.getAddress(
            keccak256(abi.encode(key, GLV))
        ));

        withdrawal.setLongTokenSwapPath(dataStore.getAddressArray(
            keccak256(abi.encode(key, LONG_TOKEN_SWAP_PATH))
        ));

        withdrawal.setShortTokenSwapPath(dataStore.getAddressArray(
            keccak256(abi.encode(key, SHORT_TOKEN_SWAP_PATH))
        ));

        withdrawal.setGlvTokenAmount(dataStore.getUint(
            keccak256(abi.encode(key, GLV_TOKEN_AMOUNT))
        ));

        withdrawal.setMinLongTokenAmount(dataStore.getUint(
            keccak256(abi.encode(key, MIN_LONG_TOKEN_AMOUNT))
        ));

        withdrawal.setMinShortTokenAmount(dataStore.getUint(
            keccak256(abi.encode(key, MIN_SHORT_TOKEN_AMOUNT))
        ));

        withdrawal.setUpdatedAtTime(dataStore.getUint(
            keccak256(abi.encode(key, UPDATED_AT_TIME))
        ));

        withdrawal.setExecutionFee(dataStore.getUint(
            keccak256(abi.encode(key, EXECUTION_FEE))
        ));

        withdrawal.setCallbackGasLimit(dataStore.getUint(
            keccak256(abi.encode(key, CALLBACK_GAS_LIMIT))
        ));

        withdrawal.setSrcChainId(dataStore.getUint(
            keccak256(abi.encode(key, SRC_CHAIN_ID))
        ));

        withdrawal.setShouldUnwrapNativeToken(dataStore.getBool(
            keccak256(abi.encode(key, SHOULD_UNWRAP_NATIVE_TOKEN))
        ));

        withdrawal.setDataList(dataStore.getBytes32Array(
            keccak256(abi.encode(key, DATA_LIST))
        ));

        return withdrawal;
    }

    function set(DataStore dataStore, bytes32 key, GlvWithdrawal.Props memory withdrawal) external {
        dataStore.addBytes32(
            Keys.GLV_WITHDRAWAL_LIST,
            key
        );

        dataStore.addBytes32(
            Keys.accountGlvWithdrawalListKey(withdrawal.account()),
            key
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, ACCOUNT)),
            withdrawal.account()
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, RECEIVER)),
            withdrawal.receiver()
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, CALLBACK_CONTRACT)),
            withdrawal.callbackContract()
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, UI_FEE_RECEIVER)),
            withdrawal.uiFeeReceiver()
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, MARKET)),
            withdrawal.market()
        );

        dataStore.setAddress(
            keccak256(abi.encode(key, GLV)),
            withdrawal.glv()
        );

        dataStore.setAddressArray(
            keccak256(abi.encode(key, LONG_TOKEN_SWAP_PATH)),
            withdrawal.longTokenSwapPath()
        );

        dataStore.setAddressArray(
            keccak256(abi.encode(key, SHORT_TOKEN_SWAP_PATH)),
            withdrawal.shortTokenSwapPath()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, GLV_TOKEN_AMOUNT)),
            withdrawal.glvTokenAmount()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, MIN_LONG_TOKEN_AMOUNT)),
            withdrawal.minLongTokenAmount()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, MIN_SHORT_TOKEN_AMOUNT)),
            withdrawal.minShortTokenAmount()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, UPDATED_AT_TIME)),
            withdrawal.updatedAtTime()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, EXECUTION_FEE)),
            withdrawal.executionFee()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, CALLBACK_GAS_LIMIT)),
            withdrawal.callbackGasLimit()
        );

        dataStore.setUint(
            keccak256(abi.encode(key, SRC_CHAIN_ID)),
            withdrawal.srcChainId()
        );

        dataStore.setBool(
            keccak256(abi.encode(key, SHOULD_UNWRAP_NATIVE_TOKEN)),
            withdrawal.shouldUnwrapNativeToken()
        );

        dataStore.setBytes32Array(
            keccak256(abi.encode(key, DATA_LIST)),
            withdrawal.dataList()
        );
    }

    function remove(DataStore dataStore, bytes32 key, address account) external {
        if (!dataStore.containsBytes32(Keys.GLV_WITHDRAWAL_LIST, key)) {
            revert Errors.GlvWithdrawalNotFound(key);
        }

        dataStore.removeBytes32(
            Keys.GLV_WITHDRAWAL_LIST,
            key
        );

        dataStore.removeBytes32(
            Keys.accountGlvWithdrawalListKey(account),
            key
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, ACCOUNT))
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, RECEIVER))
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, CALLBACK_CONTRACT))
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, UI_FEE_RECEIVER))
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, MARKET))
        );

        dataStore.removeAddress(
            keccak256(abi.encode(key, GLV))
        );

        dataStore.removeAddressArray(
            keccak256(abi.encode(key, LONG_TOKEN_SWAP_PATH))
        );

        dataStore.removeAddressArray(
            keccak256(abi.encode(key, SHORT_TOKEN_SWAP_PATH))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, GLV_TOKEN_AMOUNT))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, MIN_LONG_TOKEN_AMOUNT))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, MIN_SHORT_TOKEN_AMOUNT))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, UPDATED_AT_TIME))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, EXECUTION_FEE))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, CALLBACK_GAS_LIMIT))
        );

        dataStore.removeUint(
            keccak256(abi.encode(key, SRC_CHAIN_ID))
        );

        dataStore.removeBool(
            keccak256(abi.encode(key, SHOULD_UNWRAP_NATIVE_TOKEN))
        );

        dataStore.removeBytes32Array(
            keccak256(abi.encode(key, DATA_LIST))
        );
    }

    function getGlvWithdrawalCount(DataStore dataStore) internal view returns (uint256) {
        return dataStore.getBytes32Count(Keys.GLV_WITHDRAWAL_LIST);
    }

    function getGlvWithdrawalKeys(DataStore dataStore, uint256 start, uint256 end) internal view returns (bytes32[] memory) {
        return dataStore.getBytes32ValuesAt(Keys.GLV_WITHDRAWAL_LIST, start, end);
    }

    function getAccountGlvWithdrawalCount(DataStore dataStore, address account) internal view returns (uint256) {
        return dataStore.getBytes32Count(Keys.accountGlvWithdrawalListKey(account));
    }

    function getAccountGlvWithdrawalKeys(DataStore dataStore, address account, uint256 start, uint256 end) internal view returns (bytes32[] memory) {
        return dataStore.getBytes32ValuesAt(Keys.accountGlvWithdrawalListKey(account), start, end);
    }
}

// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../../withdrawal/ExecuteWithdrawalUtils.sol";

import "../../nonce/NonceUtils.sol";

import "../GlvVault.sol";
import "../GlvUtils.sol";
import "./GlvWithdrawalStoreUtils.sol";
import "./GlvWithdrawalEventUtils.sol";
import "./IGlvWithdrawalUtils.sol";

library GlvWithdrawalUtils {
    using GlvWithdrawal for GlvWithdrawal.Props;
    using SafeCast for int256;
    using SafeCast for uint256;
    using EventUtils for EventUtils.UintItems;
    using EventUtils for EventUtils.AddressItems;

    struct ExecuteGlvWithdrawalParams {
        DataStore dataStore;
        EventEmitter eventEmitter;
        MultichainVault multichainVault;
        GlvVault glvVault;
        IOracle oracle;
        ISwapHandler swapHandler;
        bytes32 key;
        uint256 startingGas;
        address keeper;
    }

    struct ExecuteGlvWithdrawalCache {
        uint256 glvValue;
        uint256 marketCount;
        uint256 oraclePriceCount;
        uint256 marketTokenAmount;
    }

    struct CancelGlvWithdrawalParams {
        DataStore dataStore;
        EventEmitter eventEmitter;
        MultichainVault multichainVault;
        GlvVault glvVault;
        bytes32 key;
        address keeper;
        uint256 startingGas;
        string reason;
        bytes reasonBytes;
    }

    function createGlvWithdrawal(
        DataStore dataStore,
        EventEmitter eventEmitter,
        GlvVault glvVault,
        address account,
        uint256 srcChainId,
        IGlvWithdrawalUtils.CreateGlvWithdrawalParams memory params
    ) external returns (bytes32) {
        AccountUtils.validateAccount(account);
        GlvUtils.validateGlv(dataStore, params.addresses.glv);
        GlvUtils.validateGlvMarket(dataStore, params.addresses.glv, params.addresses.market, false);

        MarketUtils.validateEnabledMarket(dataStore, params.addresses.market);
        MarketUtils.validateSwapPath(dataStore, params.addresses.longTokenSwapPath);
        MarketUtils.validateSwapPath(dataStore, params.addresses.shortTokenSwapPath);

        address wnt = TokenUtils.wnt(dataStore);
        uint256 wntAmount = glvVault.recordTransferIn(wnt);
        if (wntAmount < params.executionFee) {
            revert Errors.InsufficientWntAmountForExecutionFee(wntAmount, params.executionFee);
        }
        params.executionFee = wntAmount;

        AccountUtils.validateReceiver(params.addresses.receiver);

        uint256 glvTokenAmount = glvVault.recordTransferIn(params.addresses.glv);

        if (glvTokenAmount == 0) {
            revert Errors.EmptyGlvWithdrawalAmount();
        }

        GlvWithdrawal.Props memory glvWithdrawal = GlvWithdrawal.Props(
            GlvWithdrawal.Addresses({
                account: account,
                glv: params.addresses.glv,
                receiver: params.addresses.receiver,
                callbackContract: params.addresses.callbackContract,
                uiFeeReceiver: params.addresses.uiFeeReceiver,
                market: params.addresses.market,
                longTokenSwapPath: params.addresses.longTokenSwapPath,
                shortTokenSwapPath: params.addresses.shortTokenSwapPath
            }),
            GlvWithdrawal.Numbers({
                glvTokenAmount: glvTokenAmount,
                minLongTokenAmount: params.minLongTokenAmount,
                minShortTokenAmount: params.minShortTokenAmount,
                updatedAtTime: Chain.currentTimestamp(),
                executionFee: params.executionFee,
                callbackGasLimit: params.callbackGasLimit,
                srcChainId: srcChainId
            }),
            GlvWithdrawal.Flags({shouldUnwrapNativeToken: params.shouldUnwrapNativeToken}),
            params.dataList
        );

        CallbackUtils.validateCallbackGasLimit(dataStore, params.callbackGasLimit);

        uint256 marketCount = GlvUtils.getGlvMarketCount(dataStore, glvWithdrawal.glv());
        GasUtils.validateExecutionFee(
            dataStore,
            GasUtils.estimateExecuteGlvWithdrawalGasLimit(dataStore, glvWithdrawal, marketCount), // estimatedGasLimit
            params.executionFee,
            GasUtils.estimateGlvWithdrawalOraclePriceCount(marketCount, params.addresses.longTokenSwapPath.length + params.addresses.shortTokenSwapPath.length) // oraclePriceCount
        );

        bytes32 key = NonceUtils.getNextKey(dataStore);

        GlvWithdrawalStoreUtils.set(dataStore, key, glvWithdrawal);

        GlvWithdrawalEventUtils.emitGlvWithdrawalCreated(eventEmitter, key, glvWithdrawal);

        return key;
    }

    function executeGlvWithdrawal(
        ExecuteGlvWithdrawalParams memory params,
        GlvWithdrawal.Props memory glvWithdrawal
    ) external {
        // 63/64 gas is forwarded to external calls, reduce the startingGas to account for this
        params.startingGas -= gasleft() / 63;

        GlvWithdrawalStoreUtils.remove(params.dataStore, params.key, glvWithdrawal.account());

        ExecuteGlvWithdrawalCache memory cache;
        cache.marketTokenAmount = _getMarketTokenAmount(params.dataStore, params.oracle, glvWithdrawal);

        // burn GLV tokens before executing withdrawal
        // for both GLV amount and token amounts will be correct inside the receive() function
        // if receiver is a contract
        GlvToken(payable(glvWithdrawal.glv())).burn(address(params.glvVault), glvWithdrawal.glvTokenAmount());
        params.glvVault.syncTokenBalance(glvWithdrawal.glv());

        IExecuteWithdrawalUtils.ExecuteWithdrawalResult memory withdrawalResult = _processMarketWithdrawal(
            params,
            glvWithdrawal,
            cache.marketTokenAmount
        );

        GlvWithdrawalEventUtils.emitGlvWithdrawalExecuted(params.eventEmitter, params.key, glvWithdrawal.account());

        cache.glvValue = GlvUtils.getGlvValue(
            params.dataStore,
            params.oracle,
            glvWithdrawal.glv(),
            true
        );
        GlvEventUtils.emitGlvValueUpdated(
            params.eventEmitter,
            glvWithdrawal.glv(),
            cache.glvValue,
            GlvToken(payable(glvWithdrawal.glv())).totalSupply()
        );

        EventUtils.EventLogData memory eventData;
        eventData.addressItems.initItems(2);
        eventData.addressItems.setItem(0, "outputToken", withdrawalResult.outputToken);
        eventData.addressItems.setItem(1, "secondaryOutputToken", withdrawalResult.secondaryOutputToken);
        eventData.uintItems.initItems(2);
        eventData.uintItems.setItem(0, "outputAmount", withdrawalResult.outputAmount);
        eventData.uintItems.setItem(1, "secondaryOutputAmount", withdrawalResult.secondaryOutputAmount);
        CallbackUtils.afterGlvWithdrawalExecution(params.key, glvWithdrawal, eventData);

        cache.marketCount = GlvUtils.getGlvMarketCount(params.dataStore, glvWithdrawal.glv());
        cache.oraclePriceCount = GasUtils.estimateGlvWithdrawalOraclePriceCount(
            cache.marketCount,
            glvWithdrawal.longTokenSwapPath().length + glvWithdrawal.shortTokenSwapPath().length
        );

        GasUtils.payExecutionFee(
            GasUtils.PayExecutionFeeContracts(
                params.dataStore,
                params.eventEmitter,
                params.multichainVault,
                params.glvVault
            ),
            params.key,
            glvWithdrawal.callbackContract(),
            glvWithdrawal.executionFee(),
            params.startingGas,
            cache.oraclePriceCount,
            params.keeper,
            glvWithdrawal.receiver(),
            glvWithdrawal.srcChainId()
        );
    }

    function _processMarketWithdrawal(
        ExecuteGlvWithdrawalParams memory params,
        GlvWithdrawal.Props memory glvWithdrawal,
        uint256 marketTokenAmount
    ) private returns (IExecuteWithdrawalUtils.ExecuteWithdrawalResult memory) {

        Withdrawal.Props memory withdrawal = Withdrawal.Props(
            Withdrawal.Addresses({
                account: glvWithdrawal.glv(),
                receiver: glvWithdrawal.receiver(),
                callbackContract: address(0),
                uiFeeReceiver: glvWithdrawal.uiFeeReceiver(),
                market: glvWithdrawal.market(),
                longTokenSwapPath: glvWithdrawal.longTokenSwapPath(),
                shortTokenSwapPath: glvWithdrawal.shortTokenSwapPath()
            }),
            Withdrawal.Numbers({
                minLongTokenAmount: glvWithdrawal.minLongTokenAmount(),
                minShortTokenAmount: glvWithdrawal.minShortTokenAmount(),
                marketTokenAmount: marketTokenAmount,
                updatedAtTime: glvWithdrawal.updatedAtTime(),
                executionFee: 0,
                callbackGasLimit: 0,
                srcChainId: glvWithdrawal.srcChainId()
            }),
            Withdrawal.Flags({shouldUnwrapNativeToken: glvWithdrawal.shouldUnwrapNativeToken()}),
            glvWithdrawal.dataList()
        );

        bytes32 withdrawalKey = NonceUtils.getNextKey(params.dataStore);
        params.dataStore.addBytes32(Keys.WITHDRAWAL_LIST, withdrawalKey);
        WithdrawalEventUtils.emitWithdrawalCreated(
            params.eventEmitter,
            withdrawalKey,
            withdrawal,
            Withdrawal.WithdrawalType.Glv
        );

        Bank(payable(glvWithdrawal.glv())).transferOut(
            glvWithdrawal.market(),
            address(params.glvVault),
            marketTokenAmount
        );
        params.glvVault.syncTokenBalance(glvWithdrawal.market());

        IExecuteWithdrawalUtils.ExecuteWithdrawalParams memory executeWithdrawalParams = IExecuteWithdrawalUtils
            .ExecuteWithdrawalParams({
                dataStore: params.dataStore,
                eventEmitter: params.eventEmitter,
                multichainVault: params.multichainVault,
                withdrawalVault: WithdrawalVault(payable(params.glvVault)),
                oracle: params.oracle,
                swapHandler: params.swapHandler,
                key: withdrawalKey,
                keeper: params.keeper,
                startingGas: params.startingGas,
                swapPricingType: ISwapPricingUtils.SwapPricingType.Withdrawal
            });

        return ExecuteWithdrawalUtils.executeWithdrawal(executeWithdrawalParams, withdrawal);
    }

    function _getMarketTokenAmount(
        DataStore dataStore,
        IOracle oracle,
        GlvWithdrawal.Props memory glvWithdrawal
    ) internal view returns (uint256) {
        uint256 glvValue = GlvUtils.getGlvValue(
            dataStore,
            oracle,
            glvWithdrawal.glv(),
            false // maximize
        );
        uint256 glvSupply = GlvToken(payable(glvWithdrawal.glv())).totalSupply();
        uint256 glvTokenUsd = GlvUtils.glvTokenAmountToUsd(glvWithdrawal.glvTokenAmount(), glvValue, glvSupply);

        Market.Props memory market = MarketUtils.getEnabledMarket(dataStore, glvWithdrawal.market());
        MarketPoolValueInfo.Props memory poolValueInfo = MarketUtils.getPoolValueInfo(
            dataStore,
            market,
            oracle.getPrimaryPrice(market.indexToken),
            oracle.getPrimaryPrice(market.longToken),
            oracle.getPrimaryPrice(market.shortToken),
            Keys.MAX_PNL_FACTOR_FOR_WITHDRAWALS,
            true // maximize
        );
        uint256 marketTokenAmount = MarketUtils.usdToMarketTokenAmount(
            glvTokenUsd,
            poolValueInfo.poolValue.toUint256(),
            ERC20(market.marketToken).totalSupply()
        );

        return marketTokenAmount;
    }

    function cancelGlvWithdrawal(CancelGlvWithdrawalParams memory params) external {
        // 63/64 gas is forwarded to external calls, reduce the startingGas to account for this
        params.startingGas -= gasleft() / 63;

        GlvWithdrawal.Props memory glvWithdrawal = GlvWithdrawalStoreUtils.get(params.dataStore, params.key);
        GlvWithdrawalStoreUtils.remove(params.dataStore, params.key, glvWithdrawal.account());

        if (glvWithdrawal.srcChainId() == 0) {
            params.glvVault.transferOut(
                glvWithdrawal.glv(),
                glvWithdrawal.account(),
                glvWithdrawal.glvTokenAmount(),
                false // shouldUnwrapNativeToken
            );
        } else {
            params.glvVault.transferOut(
                glvWithdrawal.glv(),
                address(params.multichainVault),
                glvWithdrawal.glvTokenAmount(),
                false // shouldUnwrapNativeToken
            );
            MultichainUtils.recordTransferIn(
                params.dataStore,
                params.eventEmitter,
                params.multichainVault,
                glvWithdrawal.glv(),
                glvWithdrawal.account(),
                0 // srcChainId is the current block.chainId
            );
        }


        GlvWithdrawalEventUtils.emitGlvWithdrawalCancelled(
            params.eventEmitter,
            params.key,
            glvWithdrawal.account(),
            params.reason,
            params.reasonBytes
        );

        EventUtils.EventLogData memory eventData;
        CallbackUtils.afterGlvWithdrawalCancellation(params.key, glvWithdrawal, eventData);

        uint256 marketCount = GlvUtils.getGlvMarketCount(params.dataStore, glvWithdrawal.glv());
        GasUtils.payExecutionFee(
            GasUtils.PayExecutionFeeContracts(
                params.dataStore,
                params.eventEmitter,
                params.multichainVault,
                params.glvVault
            ),
            params.key,
            glvWithdrawal.callbackContract(),
            glvWithdrawal.executionFee(),
            params.startingGas,
            GasUtils.estimateGlvWithdrawalOraclePriceCount(
                marketCount,
                glvWithdrawal.longTokenSwapPath().length + glvWithdrawal.shortTokenSwapPath().length
            ),
            params.keeper,
            glvWithdrawal.receiver(),
            glvWithdrawal.srcChainId()
        );
    }
}

