// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../router/Router.sol";
import "../router/relay/SubaccountRouterUtils.sol";
import "./MultichainRouter.sol";

contract MultichainSubaccountRouter is MultichainRouter {
    mapping(address => uint256) public subaccountApprovalNonces;

    constructor(
        BaseConstructorParams memory params
    ) MultichainRouter(params) BaseRouter(params.router, params.roleStore, params.dataStore, params.eventEmitter) {}

    // @note all params except subaccount/srcChainId should be part of the corresponding struct hash
    function batch(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account,
        uint256 srcChainId,
        address subaccount,
        IRelayUtils.BatchParams calldata params
    ) external nonReentrant withRelay(relayParams, account, srcChainId, true) returns (bytes32[] memory) {
        _handleBatch(
            relayParams,
            subaccountApproval,
            account,
            srcChainId,
            subaccount,
            params
        );

        return _batch(
            account,
            srcChainId,
            params.createOrderParamsList,
            params.updateOrderParamsList,
            params.cancelOrderKeys,
            true // isSubaccount
        );
    }

    // @dev needed to keep `batch` under the stack limit
    function _handleBatch(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account,
        uint256 srcChainId,
        address subaccount,
        IRelayUtils.BatchParams calldata params
    ) private {
        bytes32 structHash = RelayUtils.getBatchStructHash(relayParams, subaccountApproval, account, params);
        _validateCall(relayParams, subaccount, structHash, srcChainId);

        for (uint256 i = 0; i < params.createOrderParamsList.length; i++) {
            SubaccountRouterUtils.validateCreateOrderParams(account, params.createOrderParamsList[i]);
        }

        uint256 actionsCount = params.createOrderParamsList.length +
            params.updateOrderParamsList.length +
            params.cancelOrderKeys.length;

        _handleSubaccountOrderAction(account, srcChainId, subaccount, actionsCount, subaccountApproval);
    }

    // @note all params except subaccount/srcChainId should be part of the corresponding struct hash
    function createOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        IBaseOrderUtils.CreateOrderParams calldata params
    ) external nonReentrant withRelay(relayParams, account, srcChainId, true) returns (bytes32) {
        _handleCreateOrder(relayParams, subaccountApproval, account, srcChainId, subaccount, params);

        return
            _createOrder(
                account,
                srcChainId,
                params,
                true // isSubaccount
            );
    }

    // @dev needed to keep `createOrder` under the stack limit
    function _handleCreateOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        IBaseOrderUtils.CreateOrderParams calldata params
    ) private {
        bytes32 structHash = RelayUtils.getCreateOrderStructHash(relayParams, subaccountApproval, account, params);
        _validateCall(relayParams, subaccount, structHash, srcChainId);
        SubaccountRouterUtils.validateCreateOrderParams(account, params);
        _handleSubaccountOrderAction(account, srcChainId, subaccount, 1, subaccountApproval);
    }

    // @note all params except subaccount/srcChainId should be part of the corresponding struct hash
    function updateOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        IRelayUtils.UpdateOrderParams calldata params
    ) external nonReentrant withRelay(relayParams, account, srcChainId, true) {
        _handleUpdateOrder(relayParams, subaccountApproval, account, srcChainId, subaccount, params);
        _updateOrder(
            account,
            params,
            true // isSubaccount
        );
    }

    // @dev needed to keep `updateOrder` under the stack limit
    function _handleUpdateOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        IRelayUtils.UpdateOrderParams calldata params
    ) private {
        bytes32 structHash = RelayUtils.getUpdateOrderStructHash(relayParams, subaccountApproval, account, params);
        _validateCall(relayParams, subaccount, structHash, srcChainId);
        _handleSubaccountOrderAction(account, srcChainId, subaccount, 1, subaccountApproval);
    }

    // @note all params except subaccount/srcChainId should be part of the corresponding struct hash
    function cancelOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        bytes32 key
    ) external nonReentrant withRelay(relayParams, account, srcChainId, true) {
        _handleCancelOrder(relayParams, subaccountApproval, account, srcChainId, subaccount, key);
        _cancelOrder(account, key);
    }

    // @dev needed to keep `cancelOrder` under the stack limit
    function _handleCancelOrder(
        IRelayUtils.RelayParams calldata relayParams,
        SubaccountApproval calldata subaccountApproval,
        address account, // main account
        uint256 srcChainId,
        address subaccount,
        bytes32 key
    ) private {
        bytes32 structHash = RelayUtils.getCancelOrderStructHash(relayParams, subaccountApproval, account, key);
        _validateCall(relayParams, subaccount, structHash, srcChainId);
        _handleSubaccountOrderAction(account, srcChainId, subaccount, 1, subaccountApproval);
    }

    // @note all params except account/srcChainId should be part of the corresponding struct hash
    function removeSubaccount(
        IRelayUtils.RelayParams calldata relayParams,
        address account,
        uint256 srcChainId,
        address subaccount
    ) external nonReentrant withRelay(relayParams, account, srcChainId, false) {
        // isSubaccount=false is passed to `withRelay` modifier because this action is signed by the main account
        bytes32 structHash = RelayUtils.getRemoveSubaccountStructHash(relayParams, subaccount);
        _validateCall(relayParams, account, structHash, srcChainId);

        SubaccountUtils.removeSubaccount(dataStore, eventEmitter, account, subaccount);
    }

    function _handleSubaccountOrderAction(
        address account,
        uint256 srcChainId,
        address subaccount,
        uint256 actionsCount,
        SubaccountApproval calldata subaccountApproval
    ) private {
        SubaccountRouterUtils.handleSubaccountAction(
            dataStore,
            eventEmitter,
            account,
            srcChainId,
            subaccount,
            Keys.SUBACCOUNT_ORDER_ACTION, // actionType
            actionsCount,
            subaccountApproval,
            subaccountApprovalNonces
        );
    }
}

contracts/multichain/MultichainTransferR.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./MultichainRouter.sol";
import "./IMultichainTransferRouter.sol";

contract MultichainTransferRouter is IMultichainTransferRouter, Initializable, MultichainRouter {
    IMultichainProvider public multichainProvider;

    constructor(
        BaseConstructorParams memory params
    ) MultichainRouter(params) BaseRouter(params.router, params.roleStore, params.dataStore, params.eventEmitter) {
        // leave empty, use initialize instead
    }

    function initialize(address _multichainProvider) external initializer {
        if (_multichainProvider == address(0)) {
            revert Errors.InvalidMultichainProvider(address(0));
        }
        multichainProvider = IMultichainProvider(_multichainProvider);
    }

    /**
     * payable function so that it can be called as a multicall
     * this would be used to move user's funds from their Arbitrum account into their multichain balance
     * @dev payable is necessary because, when bridging in WNT the user sends ETH along with the transaction (via multicall)
     */
    function bridgeIn(address account, address token) external payable nonReentrant {
        uint256 amount = MultichainUtils.recordTransferIn(
            dataStore,
            eventEmitter,
            multichainVault,
            token,
            account,
            0 // srcChainId is the current block.chainId
        );
        MultichainEventUtils.emitMultichainBridgeIn(
            eventEmitter,
            address(0),
            token,
            account,
            amount,
            0 // srcChainId is the current block.chainId
        );
    }

    /*
     * Bridge out funds recorded under the account
     * Can be used for same-chain or cross-chain withdrawals
     */
    function bridgeOut(
        IRelayUtils.RelayParams calldata relayParams,
        address account,
        uint256 srcChainId,
        IRelayUtils.BridgeOutParams calldata params
    ) external nonReentrant withRelay(relayParams, account, srcChainId, false) {
        bytes32 structHash = RelayUtils.getBridgeOutStructHash(relayParams, params);
        _validateCall(relayParams, account, structHash, srcChainId);

        _bridgeOut(account, srcChainId, params);
    }

    /*
     * Bridge out funds recorded under the account
     * Used to automatically bridge out GM/GLV token after executeDeposit/executeGlvDeposit
     */
    function bridgeOutFromController(
        IRelayUtils.RelayParams calldata relayParams,
        address account,
        uint256 srcChainId,
        IRelayUtils.BridgeOutParams calldata params
    ) external nonReentrant onlyController {
        _validateCallWithoutSignature(relayParams, srcChainId);

        _bridgeOut(account, srcChainId, params);
    }

    /*
     * Bridge out funds recorded under the account OR the smart wallet
     * Can be used for same-chain withdrawals only
     * This would be used by the smart wallets to withdraw funds from the multichain vault
     */
    function transferOut(IRelayUtils.BridgeOutParams calldata params) external nonReentrant {
        address account = msg.sender;
        _bridgeOut(account, block.chainid, params);
    }

    function _bridgeOut(address account, uint256 srcChainId, IRelayUtils.BridgeOutParams calldata params) internal {
        if (srcChainId == block.chainid) {
            // same-chain withdrawal: funds are sent directly to the user's wallet
            MultichainUtils.transferOut(
                dataStore,
                eventEmitter,
                multichainVault,
                params.token,
                account,
                account, // receiver
                params.amount,
                srcChainId
            );

            MultichainEventUtils.emitMultichainBridgeOut(
                eventEmitter,
                address(0), // provider
                params.token,
                account,
                params.amount, // amount
                0 // srcChainId is the current block.chainId
            );
        } else {
            // cross-chain withdrawal: using the multichain provider, funds are bridged to the src chain
            MultichainUtils.validateMultichainProvider(dataStore, params.provider);

            // transfer funds (amount + bridging fee) from user's multichain balance to multichainProvider
            // and execute the bridge out to srcChain
            uint256 amountOut = multichainProvider.bridgeOut(
                account,
                IRelayUtils.BridgeOutParams({
                    token: params.token,
                    amount: params.amount,
                    provider: params.provider,
                    data: params.data
                })
            );

            MultichainEventUtils.emitMultichainBridgeOut(
                eventEmitter,
                address(multichainProvider),
                params.token,
                account,
                amountOut, // amount
                srcChainId
            );
        }
    }
}

contracts/multichain/MultichainUtils.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../data/DataStore.sol";
import "../data/Keys.sol";
import "../event/EventEmitter.sol";

import "./MultichainVault.sol";
import "./MultichainEventUtils.sol";
import "./IMultichainProvider.sol";

/**
 * @title MultichainUtils
 */
library MultichainUtils {
    using SafeERC20 for IERC20;

    /**
     * Records a deposit from another chain. IMultichainProvider has CONTROLLER role
     * @param multichainProvider the multichain provider contract
     * @param token address of the token being deposited
     * @param account user address on the source chain
     * @param amount the amount of tokens being deposited
     * @param srcChainId id of the source chain
     */
    function recordBridgeIn(
        DataStore dataStore,
        EventEmitter eventEmitter,
        MultichainVault multichainVault,
        IMultichainProvider multichainProvider,
        address token,
        address account,
        uint256 amount,
        uint256 srcChainId
    ) external {
        if (amount == 0) {
            revert Errors.EmptyMultichainTransferInAmount(account, token);
        }

        // token should have been transferred to multichainVault by IMultichainProvider
        amount = multichainVault.recordTransferIn(token);

        _increaseMultichainBalance(dataStore, eventEmitter, account, token, amount, srcChainId);

        MultichainEventUtils.emitMultichainBridgeIn(
            eventEmitter,
            address(multichainProvider),
            token,
            account,
            amount,
            srcChainId
        );
    }

    /**
     * Records a transfer of tokens into the multichain vault
     * @param account user address on the source chain
     * @param token address of the token being transferred
     * @param srcChainId id of the source chain
     */
    function recordTransferIn(
        DataStore dataStore,
        EventEmitter eventEmitter,
        MultichainVault multichainVault,
        address token,
        address account,
        uint256 srcChainId
    ) external returns (uint256) {
        // token should have been transferred to multichainVault
        uint256 amount = multichainVault.recordTransferIn(token);
        if (amount == 0) {
            return 0;
        }

        _increaseMultichainBalance(dataStore, eventEmitter, account, token, amount, srcChainId);

        return amount;
    }

    function _increaseMultichainBalance(
        DataStore dataStore,
        EventEmitter eventEmitter,
        address account,
        address token,
        uint256 amount,
        uint256 srcChainId
    ) private {
        dataStore.incrementUint(Keys.multichainBalanceKey(account, token), amount);
        MultichainEventUtils.emitMultichainTransferIn(eventEmitter, token, account, amount, srcChainId);
    }

    /**
     * @dev transfer the specified amount of tokens from user's multichain balance to receiver
     * @param token the token to transfer
     * @param account the account for which the multichain balance is decreased
     * @param receiver the account to transfer to
     * @param amount the amount of tokens to transfer
     * @param srcChainId the source chain id
     */
    function transferOut(
        DataStore dataStore,
        EventEmitter eventEmitter,
        MultichainVault multichainVault,
        address token,
        address account,
        address receiver,
        uint256 amount,
        uint256 srcChainId
    ) external {
        if (amount == 0) {
            return;
        }

        uint256 balance = getMultichainBalanceAmount(dataStore, account, token);
        if (balance < amount) {
            revert Errors.InsufficientMultichainBalance(account, token, balance, amount);
        }

        dataStore.decrementUint(Keys.multichainBalanceKey(account, token), amount);
        multichainVault.transferOut(token, receiver, amount);
        MultichainEventUtils.emitMultichainTransferOut(eventEmitter, token, account, receiver, amount, srcChainId);
    }

    function getMultichainBalanceAmount(
        DataStore dataStore,
        address account,
        address token
    ) public view returns (uint256) {
        return dataStore.getUint(Keys.multichainBalanceKey(account, token));
    }

    function validateMultichainProvider(DataStore dataStore, address provider) external view {
        bytes32 providerKey = Keys.isMultichainProviderEnabledKey(provider);
        if (!dataStore.getBool(providerKey)) {
            revert Errors.InvalidMultichainProvider(provider);
        }
    }

    function validateMultichainEndpoint(DataStore dataStore, address endpoint) external view {
        bytes32 endpointKey = Keys.isMultichainEndpointEnabledKey(endpoint);
        if (!dataStore.getBool(endpointKey)) {
            revert Errors.InvalidMultichainEndpoint(endpoint);
        }
    }
}

