// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "../market/MarketStoreUtils.sol";

import "../position/Position.sol";
import "../position/PositionUtils.sol";
import "../position/PositionStoreUtils.sol";

import "../market/MarketUtils.sol";
import "../market/Market.sol";
import "./ReaderPricingUtils.sol";

library ReaderPositionUtils {
    using Position for Position.Props;
    using SafeCast for uint256;

    struct PositionInfo {
        bytes32 positionKey;
        Position.Props position;
        PositionPricingUtils.PositionFees fees;
        ReaderPricingUtils.ExecutionPriceResult executionPriceResult;
        int256 basePnlUsd;
        int256 uncappedBasePnlUsd;
        int256 pnlAfterPriceImpactUsd;
    }

    struct GetPositionInfoCache {
        Market.Props market;
        Price.Props collateralTokenPrice;
        uint256 pendingBorrowingFeeUsd;
    }

    function getNextBorrowingFees(
        DataStore dataStore,
        Position.Props memory position,
        Market.Props memory market,
        MarketUtils.MarketPrices memory prices
    ) internal view returns (uint256) {
        return MarketUtils.getNextBorrowingFees(
            dataStore,
            position,
            market,
            prices
        );
    }

    function getBorrowingFees(
        DataStore dataStore,
        Price.Props memory collateralTokenPrice,
        uint256 borrowingFeeUsd
    ) internal view returns (PositionPricingUtils.PositionBorrowingFees memory) {
        return PositionPricingUtils.getBorrowingFees(
            dataStore,
            collateralTokenPrice,
            borrowingFeeUsd
        );
    }

    function getPositionInfoList(
        DataStore dataStore,
        IReferralStorage referralStorage,
        bytes32[] memory positionKeys,
        MarketUtils.MarketPrices[] memory prices,
        address uiFeeReceiver
    ) external view returns (ReaderPositionUtils.PositionInfo[] memory) {
        ReaderPositionUtils.PositionInfo[] memory positionInfoList = new ReaderPositionUtils.PositionInfo[](positionKeys.length);
        for (uint256 i; i < positionKeys.length; i++) {
            bytes32 positionKey = positionKeys[i];
            positionInfoList[i] = getPositionInfo(
                dataStore,
                referralStorage,
                positionKey,
                prices[i],
                0, // sizeDeltaUsd
                uiFeeReceiver,
                true // usePositionSizeAsSizeDeltaUsd
            );
            positionInfoList[i].positionKey = positionKey;
        }

        return positionInfoList;
    }

    function getAccountPositionInfoList(
        DataStore dataStore,
        IReferralStorage referralStorage,
        address account,
        address[] memory markets,
        MarketUtils.MarketPrices[] memory marketPrices,
        address uiFeeReceiver,
        uint256 start,
        uint256 end
    ) external view returns (ReaderPositionUtils.PositionInfo[] memory) {
        bytes32[] memory positionKeys = PositionStoreUtils.getAccountPositionKeys(dataStore, account, start, end);
        ReaderPositionUtils.PositionInfo[] memory positionInfoList = new ReaderPositionUtils.PositionInfo[](positionKeys.length);
        for (uint256 i; i < positionKeys.length; i++) {
            bytes32 positionKey = positionKeys[i];
            Position.Props memory position = PositionStoreUtils.get(dataStore, positionKey);
            MarketUtils.MarketPrices memory prices = _getMarketPricesByAddress(markets, marketPrices, position.market());
            positionInfoList[i] = getPositionInfo(
                dataStore,
                referralStorage,
                position,
                prices,
                0, // sizeDeltaUsd
                uiFeeReceiver,
                true // usePositionSizeAsSizeDeltaUsd
            );
            positionInfoList[i].positionKey = positionKey;
        }

        return positionInfoList;
    }

    function _getMarketPricesByAddress(
        address[] memory markets,
        MarketUtils.MarketPrices[] memory marketPrices,
        address market
    ) internal pure returns (MarketUtils.MarketPrices memory) {
        for (uint256 i = 0; i < markets.length; i++) {
            address currentMarket = markets[i];
            if (currentMarket == market) {
                return marketPrices[i];
            }
        }

        revert Errors.EmptyMarketPrice(market);
    }

    function getNextFundingAmountPerSize(
        DataStore dataStore,
        Market.Props memory market,
        MarketUtils.MarketPrices memory prices
    ) public view returns (MarketUtils.GetNextFundingAmountPerSizeResult memory) {
        return MarketUtils.getNextFundingAmountPerSize(
            dataStore,
            market,
            prices
        );
    }

    function getAccountPositions(
        DataStore dataStore,
        address account,
        uint256 start,
        uint256 end
    ) external view returns (Position.Props[] memory) {
        bytes32[] memory positionKeys = PositionStoreUtils.getAccountPositionKeys(dataStore, account, start, end);
        Position.Props[] memory positions = new Position.Props[](positionKeys.length);
        for (uint256 i; i < positionKeys.length; i++) {
            bytes32 positionKey = positionKeys[i];
            positions[i] = PositionStoreUtils.get(dataStore, positionKey);
        }

        return positions;
    }

    function getPositionInfo(
        DataStore dataStore,
        IReferralStorage referralStorage,
        bytes32 positionKey,
        MarketUtils.MarketPrices memory prices,
        uint256 sizeDeltaUsd,
        address uiFeeReceiver,
        bool usePositionSizeAsSizeDeltaUsd
    ) public view returns (PositionInfo memory) {
        Position.Props memory position = PositionStoreUtils.get(dataStore, positionKey);
        return getPositionInfo(
            dataStore,
            referralStorage,
            position,
            prices,
            sizeDeltaUsd,
            uiFeeReceiver,
            usePositionSizeAsSizeDeltaUsd
        );
    }

    function getPositionInfo(
        DataStore dataStore,
        IReferralStorage referralStorage,
        Position.Props memory position,
        MarketUtils.MarketPrices memory prices,
        uint256 sizeDeltaUsd,
        address uiFeeReceiver,
        bool usePositionSizeAsSizeDeltaUsd
    ) internal view returns (PositionInfo memory) {
        if (position.account() == address(0)) {
            revert Errors.EmptyPosition();
        }

        PositionInfo memory positionInfo;
        GetPositionInfoCache memory cache;

        positionInfo.position = position;
        cache.market = MarketStoreUtils.get(dataStore, positionInfo.position.market());
        cache.collateralTokenPrice = MarketUtils.getCachedTokenPrice(positionInfo.position.collateralToken(), cache.market, prices);

        if (usePositionSizeAsSizeDeltaUsd) {
            sizeDeltaUsd = positionInfo.position.sizeInUsd();
        }

        positionInfo.executionPriceResult = ReaderPricingUtils.getExecutionPrice(
            dataStore,
            cache.market,
            prices,
            positionInfo.position.sizeInUsd(),
            positionInfo.position.sizeInTokens(),
            -sizeDeltaUsd.toInt256(),
            positionInfo.position.isLong()
        );

        PositionPricingUtils.GetPositionFeesParams memory getPositionFeesParams = PositionPricingUtils.GetPositionFeesParams({
            dataStore: dataStore,
            referralStorage: referralStorage,
            position: positionInfo.position,
            collateralTokenPrice: cache.collateralTokenPrice,
            balanceWasImproved: positionInfo.executionPriceResult.balanceWasImproved,
            longToken: cache.market.longToken,
            shortToken: cache.market.shortToken,
            sizeDeltaUsd: sizeDeltaUsd,
            uiFeeReceiver: uiFeeReceiver,
            isLiquidation: false
        });

        positionInfo.fees = PositionPricingUtils.getPositionFees(getPositionFeesParams);

        // borrowing and funding fees need to be overwritten with pending values otherwise they
        // would be using storage values that have not yet been updated
        cache.pendingBorrowingFeeUsd = getNextBorrowingFees(dataStore, positionInfo.position, cache.market, prices);

        positionInfo.fees.borrowing = getBorrowingFees(
            dataStore,
            cache.collateralTokenPrice,
            cache.pendingBorrowingFeeUsd
        );

        MarketUtils.GetNextFundingAmountPerSizeResult memory nextFundingAmountResult = getNextFundingAmountPerSize(dataStore, cache.market, prices);

        positionInfo.fees.funding.latestFundingFeeAmountPerSize = MarketUtils.getFundingFeeAmountPerSize(
            dataStore,
            positionInfo.position.market(),
            positionInfo.position.collateralToken(),
            positionInfo.position.isLong()
        );

        positionInfo.fees.funding.latestLongTokenClaimableFundingAmountPerSize = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            positionInfo.position.market(),
            cache.market.longToken,
            positionInfo.position.isLong()
        );

        positionInfo.fees.funding.latestShortTokenClaimableFundingAmountPerSize = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            positionInfo.position.market(),
            cache.market.shortToken,
            positionInfo.position.isLong()
        );

        // see MarketUtils.getNextFundingAmountPerSize for more info on why this multiplier is needed
        // a short summary:
        // - funding values are split based on long and short token
        // - for single token markets, these tokens are the same
        // - so when the funding values are applied in updateFundingState, they are applied twice
        // - e.g.
        //     - increase fundingFeeAmountPerSize(market, collateralToken: token0, isLong: true) by 10
        //     - increase fundingFeeAmountPerSize(market, collateralToken: token1, isLong: true) by 10
        //     - for a single token market, token0 is the same as token1, so the value would be increased by 20
        // - to avoid costs being doubled, these values are halved in MarketUtils.getNextFundingAmountPerSize
        // - the reader code needs to double the values, because in the code below the nextFundingAmountResult
        // values are applied virtually instead of the DataStore values being updated
        uint256 multiplier = cache.market.longToken == cache.market.shortToken ? 2 : 1;

        if (positionInfo.position.isLong()) {
            positionInfo.fees.funding.latestLongTokenClaimableFundingAmountPerSize += nextFundingAmountResult.claimableFundingAmountPerSizeDelta.long.longToken * multiplier;
            positionInfo.fees.funding.latestShortTokenClaimableFundingAmountPerSize += nextFundingAmountResult.claimableFundingAmountPerSizeDelta.long.shortToken * multiplier;

            if (positionInfo.position.collateralToken() == cache.market.longToken) {
                positionInfo.fees.funding.latestFundingFeeAmountPerSize += nextFundingAmountResult.fundingFeeAmountPerSizeDelta.long.longToken * multiplier;
            } else {
                positionInfo.fees.funding.latestFundingFeeAmountPerSize += nextFundingAmountResult.fundingFeeAmountPerSizeDelta.long.shortToken * multiplier;
            }
        } else {
            positionInfo.fees.funding.latestLongTokenClaimableFundingAmountPerSize += nextFundingAmountResult.claimableFundingAmountPerSizeDelta.short.longToken * multiplier;
            positionInfo.fees.funding.latestShortTokenClaimableFundingAmountPerSize += nextFundingAmountResult.claimableFundingAmountPerSizeDelta.short.shortToken * multiplier;

            if (positionInfo.position.collateralToken() == cache.market.longToken) {
                positionInfo.fees.funding.latestFundingFeeAmountPerSize += nextFundingAmountResult.fundingFeeAmountPerSizeDelta.short.longToken * multiplier;
            } else {
                positionInfo.fees.funding.latestFundingFeeAmountPerSize += nextFundingAmountResult.fundingFeeAmountPerSizeDelta.short.shortToken * multiplier;
            }
        }

        positionInfo.fees.funding = PositionPricingUtils.getFundingFees(
            positionInfo.fees.funding,
            positionInfo.position
        );

        (positionInfo.basePnlUsd, positionInfo.uncappedBasePnlUsd, /* sizeDeltaInTokens */) = PositionUtils.getPositionPnlUsd(
            dataStore,
            cache.market,
            prices,
            positionInfo.position,
            sizeDeltaUsd
        );

        positionInfo.pnlAfterPriceImpactUsd = positionInfo.executionPriceResult.priceImpactUsd + positionInfo.basePnlUsd;

        positionInfo.fees.totalCostAmountExcludingFunding =
            positionInfo.fees.positionFeeAmount
            + positionInfo.fees.borrowing.borrowingFeeAmount
            + positionInfo.fees.ui.uiFeeAmount
            - positionInfo.fees.totalDiscountAmount;

        positionInfo.fees.totalCostAmount =
            positionInfo.fees.totalCostAmountExcludingFunding
            + positionInfo.fees.funding.fundingFeeAmount;

        return positionInfo;
    }
}

contracts/reader/ReaderPricingUtils.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SignedMath.sol";

import "../position/Position.sol";
import "../position/PositionUtils.sol";
import "../market/MarketUtils.sol";
import "../market/Market.sol";
import "../pricing/SwapPricingUtils.sol";
import "../swap/SwapUtils.sol";

// @title ReaderPricingUtils
library ReaderPricingUtils {
    using SignedMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;
    using Price for Price.Props;
    using Position for Position.Props;
    using Order for Order.Props;

    struct ExecutionPriceResult {
        int256 priceImpactUsd;
        uint256 executionPrice;
        bool balanceWasImproved;
    }

    struct PositionInfo {
        Position.Props position;
        PositionPricingUtils.PositionFees fees;
        ExecutionPriceResult executionPriceResult;
        int256 basePnlUsd;
        int256 pnlAfterPriceImpactUsd;
    }

    struct GetPositionInfoCache {
        Market.Props market;
        Price.Props collateralTokenPrice;
        uint256 pendingBorrowingFeeUsd;
        int256 latestLongTokenFundingAmountPerSize;
        int256 latestShortTokenFundingAmountPerSize;
    }

    // returns amountOut, price impact, fees
    function getSwapAmountOut(
        DataStore dataStore,
        Market.Props memory market,
        MarketUtils.MarketPrices memory prices,
        address tokenIn,
        uint256 amountIn,
        address uiFeeReceiver
    ) external view returns (uint256, int256, SwapPricingUtils.SwapFees memory) {
        SwapUtils.SwapCache memory cache;

        if (tokenIn != market.longToken && tokenIn != market.shortToken) {
            revert Errors.InvalidTokenIn(tokenIn, market.marketToken);
        }

        MarketUtils.validateSwapMarket(dataStore, market);

        cache.tokenOut = MarketUtils.getOppositeToken(tokenIn, market);
        cache.tokenInPrice = MarketUtils.getCachedTokenPrice(tokenIn, market, prices);
        cache.tokenOutPrice = MarketUtils.getCachedTokenPrice(cache.tokenOut, market, prices);

        (int256 priceImpactUsd, bool balanceWasImproved) = SwapPricingUtils.getPriceImpactUsd(
            SwapPricingUtils.GetPriceImpactUsdParams(
                dataStore,
                market,
                tokenIn,
                cache.tokenOut,
                cache.tokenInPrice.midPrice(),
                cache.tokenOutPrice.midPrice(),
                (amountIn * cache.tokenInPrice.midPrice()).toInt256(),
                -(amountIn * cache.tokenInPrice.midPrice()).toInt256(),
                true // includeVirtualInventoryImpact
            )
        );

        SwapPricingUtils.SwapFees memory fees = SwapPricingUtils.getSwapFees(
            dataStore,
            market.marketToken,
            amountIn,
            balanceWasImproved,
            uiFeeReceiver,
            ISwapPricingUtils.SwapPricingType.Swap
        );

        int256 impactAmount;

        if (priceImpactUsd > 0) {
            // when there is a positive price impact factor, additional tokens from the swap impact pool
            // are withdrawn for the user
            // for example, if 50,000 USDC is swapped out and there is a positive price impact
            // an additional 100 USDC may be sent to the user
            // the swap impact pool is decreased by the used amount

            cache.amountIn = fees.amountAfterFees;

            (impactAmount, cache.cappedDiffUsd) = MarketUtils.getSwapImpactAmountWithCap(
                dataStore,
                market.marketToken,
                cache.tokenOut,
                cache.tokenOutPrice,
                priceImpactUsd
            );

            if (cache.cappedDiffUsd != 0) {
                (cache.tokenInPriceImpactAmount, /* uint256 cappedDiffUsd */) = MarketUtils.getSwapImpactAmountWithCap(
                    dataStore,
                    market.marketToken,
                    tokenIn,
                    cache.tokenInPrice,
                    cache.cappedDiffUsd.toInt256()
                );

                // this additional amountIn is already in the Market
                // it is subtracted from the swap impact pool amount
                // and the market pool amount is increased by the updated
                // amountIn below
                cache.amountIn += cache.tokenInPriceImpactAmount.toUint256();
            }

            // round amountOut down
            cache.amountOut = cache.amountIn * cache.tokenInPrice.min / cache.tokenOutPrice.max;
            cache.poolAmountOut = cache.amountOut;

            cache.amountOut += impactAmount.toUint256();
        } else {
            // when there is a negative price impact factor,
            // less of the input amount is sent to the pool
            // for example, if 10 ETH is swapped in and there is a negative price impact
            // only 9.995 ETH may be swapped in
            // the remaining 0.005 ETH will be stored in the swap impact pool

            (impactAmount, /* uint256 cappedDiffUsd */) = MarketUtils.getSwapImpactAmountWithCap(
                dataStore,
                market.marketToken,
                tokenIn,
                cache.tokenInPrice,
                priceImpactUsd
            );

            cache.amountIn = fees.amountAfterFees - (-impactAmount).toUint256();
            cache.amountOut = cache.amountIn * cache.tokenInPrice.min / cache.tokenOutPrice.max;
            cache.poolAmountOut = cache.amountOut;
        }

        return (cache.amountOut, impactAmount, fees);
    }

    function getExecutionPrice(
        DataStore dataStore,
        Market.Props memory market,
        MarketUtils.MarketPrices memory prices,
        uint256 positionSizeInUsd,
        uint256 positionSizeInTokens,
        int256 sizeDeltaUsd,
        bool isLong
    ) external view returns (ExecutionPriceResult memory) {
        PositionUtils.UpdatePositionParams memory params;

        params.contracts.dataStore = dataStore;
        params.market = market;

        params.order.setSizeDeltaUsd(sizeDeltaUsd.abs());
        params.order.setIsLong(isLong);

        bool isIncrease = sizeDeltaUsd > 0;
        bool shouldExecutionPriceBeSmaller = isIncrease ? isLong : !isLong;
        params.order.setAcceptablePrice(shouldExecutionPriceBeSmaller ? type(uint256).max : 0);

        params.position.setSizeInUsd(positionSizeInUsd);
        params.position.setSizeInTokens(positionSizeInTokens);
        params.position.setIsLong(isLong);

        ExecutionPriceResult memory result;

        if (sizeDeltaUsd > 0) {
            (result.priceImpactUsd, /* priceImpactAmount */, /* sizeDeltaInTokens */, result.executionPrice, result.balanceWasImproved) = PositionUtils.getExecutionPriceForIncrease(
                params,
                prices
            );
        } else {
             (result.priceImpactUsd, result.executionPrice, result.balanceWasImproved) = PositionUtils.getExecutionPriceForDecrease(
                params,
                prices.indexTokenPrice
            );
        }

        return result;
    }

    function getSwapPriceImpact(
        DataStore dataStore,
        Market.Props memory market,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        Price.Props memory tokenInPrice,
        Price.Props memory tokenOutPrice
    ) external view returns (int256 priceImpactUsdBeforeCap, int256 priceImpactAmount, int256 tokenInPriceImpactAmount) {
        (priceImpactUsdBeforeCap, ) = SwapPricingUtils.getPriceImpactUsd(
            SwapPricingUtils.GetPriceImpactUsdParams(
                dataStore,
                market,
                tokenIn,
                tokenOut,
                tokenInPrice.midPrice(),
                tokenOutPrice.midPrice(),
                (amountIn * tokenInPrice.midPrice()).toInt256(),
                -(amountIn * tokenInPrice.midPrice()).toInt256(),
                true // includeVirtualInventoryImpact
            )
        );

        if (priceImpactUsdBeforeCap > 0) {
            uint256 cappedDiffUsd;
            (priceImpactAmount, cappedDiffUsd) = MarketUtils.getSwapImpactAmountWithCap(
                dataStore,
                market.marketToken,
                tokenOut,
                tokenOutPrice,
                priceImpactUsdBeforeCap
            );

            if (cappedDiffUsd != 0) {
                (tokenInPriceImpactAmount, /* uint256 cappedDiffUsd */) = MarketUtils.getSwapImpactAmountWithCap(
                    dataStore,
                    market.marketToken,
                    tokenIn,
                    tokenInPrice,
                    cappedDiffUsd.toInt256()
                );
            }
        } else {
            (priceImpactAmount, /* uint256 cappedDiffUsd */) = MarketUtils.getSwapImpactAmountWithCap(
                dataStore,
                market.marketToken,
                tokenIn,
                tokenInPrice,
                priceImpactUsdBeforeCap
            );
        }

        return (priceImpactUsdBeforeCap, priceImpactAmount, tokenInPriceImpactAmount);
    }
}

contracts/reader/ReaderUtils.sol.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SignedMath.sol";

import "../data/Keys.sol";

import "../market/MarketStoreUtils.sol";

import "../position/Position.sol";

import "../order/OrderStoreUtils.sol";

import "../adl/AdlUtils.sol";

import "../market/MarketUtils.sol";
import "../market/Market.sol";

import "./ReaderPositionUtils.sol";

// @title ReaderUtils
// @dev Library for read utils functions
// convers some internal library functions into external functions to reduce
// the Reader contract size
library ReaderUtils {
    using SignedMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;
    using Price for Price.Props;
    using Position for Position.Props;
    using Order for Order.Props;

    struct VirtualInventory {
        uint256 virtualPoolAmountForLongToken;
        uint256 virtualPoolAmountForShortToken;
        int256 virtualInventoryForPositions;
    }

    struct MarketInfo {
        Market.Props market;
        uint256 borrowingFactorPerSecondForLongs;
        uint256 borrowingFactorPerSecondForShorts;
        BaseFundingValues baseFunding;
        MarketUtils.GetNextFundingAmountPerSizeResult nextFunding;
        VirtualInventory virtualInventory;
        bool isDisabled;
    }

    struct OrderInfo {
        bytes32 orderKey;
        Order.Props order;
    }

    struct BaseFundingValues {
        MarketUtils.PositionType fundingFeeAmountPerSize;
        MarketUtils.PositionType claimableFundingAmountPerSize;
    }

    function getOrder(DataStore dataStore, bytes32 key) external view returns (Order.Props memory) {
        return OrderStoreUtils.get(dataStore, key);
    }

    function getAccountOrders(
        DataStore dataStore,
        address account,
        uint256 start,
        uint256 end
    ) external view returns (OrderInfo[] memory) {
        bytes32[] memory orderKeys = OrderStoreUtils.getAccountOrderKeys(dataStore, account, start, end);
        OrderInfo[] memory orders = new OrderInfo[](orderKeys.length);
        for (uint256 i; i < orderKeys.length; i++) {
            bytes32 orderKey = orderKeys[i];
            orders[i] = OrderInfo(orderKey, OrderStoreUtils.get(dataStore, orderKey));
        }

        return orders;
    }

    function getBaseFundingValues(DataStore dataStore, Market.Props memory market) public view returns (BaseFundingValues memory) {
        BaseFundingValues memory values;

        values.fundingFeeAmountPerSize.long.longToken = MarketUtils.getFundingFeeAmountPerSize(
            dataStore,
            market.marketToken,
            market.longToken,
            true // isLong
        );

        values.fundingFeeAmountPerSize.long.shortToken = MarketUtils.getFundingFeeAmountPerSize(
            dataStore,
            market.marketToken,
            market.shortToken,
            true // isLong
        );

        values.fundingFeeAmountPerSize.short.longToken = MarketUtils.getFundingFeeAmountPerSize(
            dataStore,
            market.marketToken,
            market.longToken,
            false // isLong
        );

        values.fundingFeeAmountPerSize.short.shortToken = MarketUtils.getFundingFeeAmountPerSize(
            dataStore,
            market.marketToken,
            market.shortToken,
            false // isLong
        );

        values.claimableFundingAmountPerSize.long.longToken = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            market.marketToken,
            market.longToken,
            true // isLong
        );

        values.claimableFundingAmountPerSize.long.shortToken = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            market.marketToken,
            market.shortToken,
            true // isLong
        );

        values.claimableFundingAmountPerSize.short.longToken = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            market.marketToken,
            market.longToken,
            false // isLong
        );

        values.claimableFundingAmountPerSize.short.shortToken = MarketUtils.getClaimableFundingAmountPerSize(
            dataStore,
            market.marketToken,
            market.shortToken,
            false // isLong
        );

        return values;
    }

    function getMarkets(DataStore dataStore, uint256 start, uint256 end) external view returns (Market.Props[] memory) {
        address[] memory marketKeys = MarketStoreUtils.getMarketKeys(dataStore, start, end);
        Market.Props[] memory markets = new Market.Props[](marketKeys.length);
        for (uint256 i; i < marketKeys.length; i++) {
            address marketKey = marketKeys[i];
            Market.Props memory market = MarketStoreUtils.get(dataStore, marketKey);
            markets[i] = market;
        }

        return markets;
    }

    function getMarketInfo(
        DataStore dataStore,
        MarketUtils.MarketPrices memory prices,
        address marketKey
    ) public view returns (MarketInfo memory) {
        Market.Props memory market = MarketStoreUtils.get(dataStore, marketKey);

        uint256 borrowingFactorPerSecondForLongs = MarketUtils.getBorrowingFactorPerSecond(
            dataStore,
            market,
            prices,
            true
        );

        uint256 borrowingFactorPerSecondForShorts = MarketUtils.getBorrowingFactorPerSecond(
            dataStore,
            market,
            prices,
            false
        );

        BaseFundingValues memory baseFunding = getBaseFundingValues(dataStore, market);

        MarketUtils.GetNextFundingAmountPerSizeResult memory nextFunding = ReaderPositionUtils.getNextFundingAmountPerSize(
            dataStore,
            market,
            prices
        );

        VirtualInventory memory virtualInventory = getVirtualInventory(dataStore, market);

        bool isMarketDisabled = dataStore.getBool(Keys.isMarketDisabledKey(market.marketToken));

        return
            MarketInfo(
                market,
                borrowingFactorPerSecondForLongs,
                borrowingFactorPerSecondForShorts,
                baseFunding,
                nextFunding,
                virtualInventory,
                isMarketDisabled
            );
    }

    function getMarketInfoList(
        DataStore dataStore,
        MarketUtils.MarketPrices[] memory marketPricesList,
        uint256 start,
        uint256 end
    ) external view returns (ReaderUtils.MarketInfo[] memory) {
        address[] memory marketKeys = MarketStoreUtils.getMarketKeys(dataStore, start, end);
        ReaderUtils.MarketInfo[] memory marketInfoList = new ReaderUtils.MarketInfo[](marketKeys.length);
        for (uint256 i; i < marketKeys.length; i++) {
            MarketUtils.MarketPrices memory prices = marketPricesList[i];
            address marketKey = marketKeys[i];
            marketInfoList[i] = getMarketInfo(dataStore, prices, marketKey);
        }

        return marketInfoList;
    }

    function getVirtualInventory(
        DataStore dataStore,
        Market.Props memory market
    ) internal view returns (VirtualInventory memory) {
        (, uint256 virtualPoolAmountForLongToken, uint256 virtualPoolAmountForShortToken) = MarketUtils
            .getVirtualInventoryForSwaps(dataStore, market.marketToken);
        (, int256 virtualInventoryForPositions) = MarketUtils.getVirtualInventoryForPositions(
            dataStore,
            market.indexToken
        );

        return
            VirtualInventory(
                virtualPoolAmountForLongToken,
                virtualPoolAmountForShortToken,
                virtualInventoryForPositions
            );
    }

    function getAdlState(
        DataStore dataStore,
        address market,
        bool isLong,
        MarketUtils.MarketPrices memory prices
    ) external view returns (uint256, bool, int256, uint256) {
        uint256 latestAdlTime = AdlUtils.getLatestAdlTime(dataStore, market, isLong);
        Market.Props memory _market = MarketUtils.getEnabledMarket(dataStore, market);

        (bool shouldEnableAdl, int256 pnlToPoolFactor, uint256 maxPnlFactor) = MarketUtils.isPnlFactorExceeded(
            dataStore,
            _market,
            prices,
            isLong,
            Keys.MAX_PNL_FACTOR_FOR_ADL
        );

        return (latestAdlTime, shouldEnableAdl, pnlToPoolFactor, maxPnlFactor);
    }
}

contracts/reader/ReaderWithdrawalUtils.s.content:
// SPDX-License-Identifier: BUSL-1.1

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SignedMath.sol";

import "../data/Keys.sol";

import "../position/Position.sol";
import "../market/MarketUtils.sol";
import "../market/Market.sol";

import "../pricing/ISwapPricingUtils.sol";
import "../pricing/SwapPricingUtils.sol";

library ReaderWithdrawalUtils {
    using SignedMath for int256;
    using SafeCast for uint256;
    using SafeCast for int256;
    using Price for Price.Props;
    using Position for Position.Props;
    using Order for Order.Props;

    struct GetWithdrawalAmountOutCache {
        uint256 poolValue;
        uint256 marketTokensSupply;

        uint256 longTokenPoolAmount;
        uint256 shortTokenPoolAmount;

        uint256 longTokenPoolUsd;
        uint256 shortTokenPoolUsd;

        uint256 totalPoolUsd;

        uint256 marketTokensUsd;

        uint256 longTokenOutputUsd;
        uint256 shortTokenOutputUsd;

        uint256 longTokenOutputAmount;
        uint256 shortTokenOutputAmount;
    }

    function getWithdrawalAmountOut(
        DataStore dataStore,
        Market.Props memory market,
        MarketUtils.MarketPrices memory prices,
        uint256 marketTokenAmount,
        address uiFeeReceiver,
        ISwapPricingUtils.SwapPricingType swapPricingType
    ) external view returns (uint256, uint256) {
        GetWithdrawalAmountOutCache memory cache;

        MarketPoolValueInfo.Props memory poolValueInfo = MarketUtils.getPoolValueInfo(
            dataStore,
            market,
            prices.indexTokenPrice,
            prices.longTokenPrice,
            prices.shortTokenPrice,
            Keys.MAX_PNL_FACTOR_FOR_WITHDRAWALS,
            false
        );

        if (poolValueInfo.poolValue <= 0) {
            revert Errors.InvalidPoolValueForWithdrawal(poolValueInfo.poolValue);
        }

        cache.poolValue = poolValueInfo.poolValue.toUint256();
        cache.marketTokensSupply = MarketUtils.getMarketTokenSupply(MarketToken(payable(market.marketToken)));

        cache.longTokenPoolAmount = MarketUtils.getPoolAmount(dataStore, market, market.longToken);
        cache.shortTokenPoolAmount = MarketUtils.getPoolAmount(dataStore, market, market.shortToken);

        cache.longTokenPoolUsd = cache.longTokenPoolAmount * prices.longTokenPrice.max;
        cache.shortTokenPoolUsd = cache.shortTokenPoolAmount * prices.shortTokenPrice.max;

        cache.totalPoolUsd = cache.longTokenPoolUsd + cache.shortTokenPoolUsd;

        cache.marketTokensUsd = MarketUtils.marketTokenAmountToUsd(marketTokenAmount, cache.poolValue, cache.marketTokensSupply);

        cache.longTokenOutputUsd = Precision.mulDiv(cache.marketTokensUsd, cache.longTokenPoolUsd, cache.totalPoolUsd);
        cache.shortTokenOutputUsd = Precision.mulDiv(cache.marketTokensUsd, cache.shortTokenPoolUsd, cache.totalPoolUsd);

        cache.longTokenOutputAmount = cache.longTokenOutputUsd / prices.longTokenPrice.max;
        cache.shortTokenOutputAmount = cache.shortTokenOutputUsd / prices.shortTokenPrice.max;

        SwapPricingUtils.SwapFees memory longTokenFees = SwapPricingUtils.getSwapFees(
            dataStore,
            market.marketToken,
            cache.longTokenOutputAmount,
            false, // balanceWasImproved
            uiFeeReceiver,
            swapPricingType
        );

        SwapPricingUtils.SwapFees memory shortTokenFees = SwapPricingUtils.getSwapFees(
            dataStore,
            market.marketToken,
            cache.shortTokenOutputAmount,
            false, // balanceWasImproved
            uiFeeReceiver,
            swapPricingType
        );

        return (
            longTokenFees.amountAfterFees,
            shortTokenFees.amountAfterFees
        );
    }
}

